INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (95, "Giedrius Cikanavičius", 5, "https://lh4.googleusercontent.com/-2vR0RsBzc7k/AAAAAAAAAAI/AAAAAAAATJg/MIG2BFKTEhc/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "5$ all you can eat buffet from 11.30 to 14.00 is awesome! Girls are nice too ;)", "7 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (96, "Yongqiang Li", 2, "https://lh4.googleusercontent.com/-RFf5SQU6i7I/AAAAAAAAAAI/AAAAAAAAKME/Ske5ds3zVMg/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "I ordered the meat combo noodle with soup. Definitely no Korean element in it. Burgers may be a better option when you are in Rinco Center for quick lunch. ", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (96, "Derrick Tao", 3, "https://lh4.googleusercontent.com/-l77pGa07w0U/AAAAAAAAAAI/AAAAAAAAt_8/91PBLUV3ErE/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Lots of choices here. Pretty standard lunch place. Nothing to amazing. ", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (96, "Tu Lam", 4, "https://lh5.googleusercontent.com/-Gsv7rI3H6J4/AAAAAAAAAAI/AAAAAAAA8Sw/gY4qds0XDQw/s128-c0x00000000-cc-rp-mo-ba6/photo.jpg", "I was down here for training. We spent every day wandering around looking for something different to eat. I don't know how we ended up here. Quick service and we found a table to sit with no problem. You can't compare this to a full Korean restaurant, it was pretty good for fast food. If you don't know what to order, fall back on the bim bim bop which is a mixed rice bowl with meat and veggies.", "3 years ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (96, "Good Music Radio", 4, "https://lh3.googleusercontent.com/-sxiVTCSByVE/AAAAAAAAAAI/AAAAAAAAACQ/FoDLRdxjw4w/s128-c0x00000000-cc-rp-mo/photo.jpg", "Most of the menu is good, nothing too special, but my god, the short ribs are godly", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (96, "Tom Bellenger", 1, "https://lh4.googleusercontent.com/-0aGO6k1JoVA/AAAAAAAAAAI/AAAAAAAAoh8/Um3eMwOWV9Q/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Avoid the won ton soup and pot stickers. No flavour. The pot stickers were fat soggy and doughy. The soup was also pretty awful. Themight be better at bbq so I'd try again but avoid", "2 years ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (97, "Simon Donovan", 5, "https://lh6.googleusercontent.com/-BGGVcDgZVcI/AAAAAAAAAAI/AAAAAAAAADU/aGNsFZsfnYg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "A fantastic place! These guys donate to my kids hockey team and I have been there ever since. A great local place where you can get a quick drink with a friend and the food options are excellent the bartenders and you can always find a friendly face. I would recommend this for out-of-towners as well as local San Francisco books to find themselves downtown.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (97, "Jeremy Stover", 5, "https://lh5.googleusercontent.com/-DgSd1p8wi6Y/AAAAAAAAAAI/AAAAAAAAbiU/p2OcR-sURP8/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "This place is super close to home so we have been going a bunch. Including my last birthday dinner. Great pizza, great wine, great dessert menu. Worth a visit for sure!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (97, "cmoker1", 4, "https://lh4.googleusercontent.com/-w7vlwW6VTHM/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUAYBhkn1leKtNY4wSIPkSwEh46AQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Five of us went for pizza and salad. We walked in on a Sat night at 6 without reservations. The hostess told us that she would seat us, but we would have to leave by 8:30! Not sure what that was about. I would give the crust 4 stars, but the marinara maybe a 3 star at most. The Waiter forgot to bring the salad. The only reason I am giving this place a 4 star is because when we asked about the salad, the Waiter gave it to us on the house. The salad came after we were done eating, but it was good.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (97, "John Hutchinson", 5, "https://lh3.googleusercontent.com/-5DwPHUPkSR4/AAAAAAAAAAI/AAAAAAAABNk/WMuWCAdIsGM/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "We didn't make a reservation and there were no tables available the night we were there, but we were able to sit at the bar. The bartender was very attentive and we got it food quickly. The bruschetta avocado toast appetizer was very good and we also enjoyed our Fillmore pizza.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (97, "Rodney T", 5, "https://lh4.googleusercontent.com/-uomCAsH1JrU/AAAAAAAAAAI/AAAAAAAAeT4/-jqLvni53qE/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "I highly recommend this place. A small note, for those with children with picky taste; this is not for them. The place is two story and has an rich dark wood decor to it. The drinks are amazing, and the food is.. mouthwatering delicious. Order an appetizer, a second plate, and finish off with a pizza. Great place; especially for a party of two. I can't wait to go back.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (98, "Hoosie Ung", 4, "https://lh4.googleusercontent.com/-37fjKrXUcHo/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWktzFWDgIYTYA6e9nw_ALcZ8eqjg/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Intimate, cool, casual vibe yet small vibe at Monks Kettle. There's not a lot of food variety, but pretzel appetizer is really good - the sauces make it. Good variety of beers to pick from. Not a lot of wines at all. You can still have a good conversation with the noise level.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (98, "Kirk Hudson", 4, "https://lh4.googleusercontent.com/-1FR9aKyExjA/AAAAAAAAAAI/AAAAAAAAITg/7P77G9ULvIk/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Very knowledgeable bartenders and wait staff - they really know their food and beverages. The kitchen is always spot on; I've never had a food misstep in 10 visits! Not many places can be a craft brew destination AND. provide consistently good food for people just wanting to dine. Well done Monk's Kettle.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (98, "Jane Wang", 4, "https://lh5.googleusercontent.com/-w3Io1juG_iY/AAAAAAAAAAI/AAAAAAAAAAo/9dkDTIMa4Tk/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "We have been really into sours lately and Monk's kettle did not disappoint. After describing our preferences (stronger, darker sours in the Belgian style) to the bartender, she recommended two that were bottled in 375ml and both were on point! We also got some mussels and fries to share to round out a fun evening. Only drawback is that this place gets packed and you might be told to wait outside for a bar spot.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (98, "Bill Mulligan", 3, "https://lh6.googleusercontent.com/-R29O4TpHv5Q/AAAAAAAAAAI/AAAAAAAAABY/BC0rZXnHppg/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "They have a great selection of beers on tap that run the length of the bar. The food, while not inexpensive, is not over priced for San Francisco. It is rather small so if it is busy expect a wait. It is all hard surfaces so when it does get busy it is very loud and hard to hear. We were seated at the bar by the door which is fine except when the door man holds it open for minutes at a time in winter.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (98, "Gary Withey", 5, "https://lh4.googleusercontent.com/-OUi7SpeIMpQ/AAAAAAAAAAI/AAAAAAAADys/daN5ddDfFpk/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "fantastic selection of beers, and one of the best burgers in SF. it's not a large place so it gets crowded easily.. a great spot for a late-night drink and bite. the staff is friendly and knowledgeable. there is a tv that shows sporting events, but it's not at all a sports bar.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (99, "Ankit Mathur", 5, "https://lh5.googleusercontent.com/-qqKgYEKQ-fI/AAAAAAAAAAI/AAAAAAAAT5M/IOmscTbvyuY/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Foreign Cinema was an amazing restaurant experience. The food was very unique, the ambience was very romantic, and the quality of the ingredients was outstanding. The pasta was really good and creamy - would definitely recommend that. The calamari was quite unique - definitely worth a try.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (99, "John James", 5, "https://lh6.googleusercontent.com/-TyALt8h7yJo/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXE1iwI9NLWmITYf8KJfAu-V7WCfg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "You need to book for brunch and line up at 1040 before the 11am opening if you go on the weekend. Very interesting menu split between egg cookery entres and lighter fresh appetizers. Good wine and drink options even for the Champagne snob like me. Someone here knows their wines and food. S great upper class brunch option in a friendly communal setting. The courtyard is where it's at.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (99, "Edmund Lee", 5, "https://lh3.googleusercontent.com/-1A7imJ_pHdY/AAAAAAAAAAI/AAAAAAAAAAA/P-hMbrA49ck/s128-c0x00000000-cc-rp-mo/photo.jpg", "Cool spot great service. The good was good and price is not bad. Perfect place for a date. The waiter was nice and thoughtful.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (99, "Chelsea Robertson", 5, "https://lh6.googleusercontent.com/-1G9ywCPnZmE/AAAAAAAAAAI/AAAAAAAARYE/WSDQOVBRpqc/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Great space for an office event. The servers came around with tons of great apps and the bartenders were available to make delicious cocktails. Combined with a coat check up front made the night really nice and easy", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (99, "Ben Shafer", 5, "https://lh4.googleusercontent.com/-nqyv_ifMTW4/AAAAAAAAAAI/AAAAAAAAW1Y/Qlcvz966pSw/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Our waiter Juan was fantastic. Offered pairings with our drinks that were outstanding. More on that later. Ambiance is fantastic. We sat on the patio that has a movie playing. Charming lights and of course plenty of heaters so you don't get cold. When you first arrive you walk down a candle lit corridor. It sets the mood very nicely. The food was delicious. We started with a pear endive salad with a frise, blue cheese and vinaigrette dressing. Thier curried honey glazed fried chicken was great. I sampled my partners meal. I had the duck that was perfect. Serving portions were perfect and was a top choice while dining in SF. Get yourself a reservation.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (100, "Kelsey Non Sequitur", 5, "https://lh6.googleusercontent.com/-T11Qte7N68Q/AAAAAAAAAAI/AAAAAAAAAHE/TSZvH6EP5D0/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "The Sycamore is a different take on bottomless brunch. I'm used to it being kinda fancy (and expensive), but the Sycamore is super casual, and not nearly as pricey. Bottomless mimosas were $12 with $10 minimum of food. We got the mix 'n match sliders: two fried chicken sliders, a lamb burger, and the ground beef. The chicken slider was pretty rich. It was good, but I liked the ground beef better. It packed a lot of flavor into that tiny burger. My date preferred his lamb burger to the chicken. Our glasses were frequently refilled, and the staff was fun and friendly. They had great music, lots of 80s hits. I'd go back again.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (100, "Erin G", 5, "https://lh6.googleusercontent.com/-yB0W_rRppO0/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWecDS6uJnbMYkZS5Ak3orysE6bcQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great dive bar. Pretty spacious patio out back for nice days. Los of tasty beers- but only beer.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (100, "Tiva Samaru", 5, "https://lh4.googleusercontent.com/-qV-oy4uS2Gs/AAAAAAAAAAI/AAAAAAAAXeg/0cR5mHH5R9M/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great for brunch and Charlie (if he still works there) is amazing. They have an outdoor area. You can smoke there. Their beer selection and food are good, but the wait for the bathroom can be a pain! On another note, once, the patrons in the outdoor patio, joined in with my friends and sang happy birthday to me, so this place is a special one, particularly because of that.", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (100, "Jimmy Chan", 4, "https://lh4.googleusercontent.com/-3OTdvt_U3no/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXfappvSO-w_Rt8otkV5XX07SSnLA/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Hole in the wall vibe, brunch is pretty good, bottomless mimosas for $14! Gets pretty busy, it's open seating so first come first served, they also have an outdoor seating area out back.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (100, "Vincent Furia", 5, "https://lh3.googleusercontent.com/-0FTEk8mRN18/AAAAAAAAAAI/AAAAAAAACt0/ADCtIuWDCmI/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Try the Brussel Sprouts! Great selection of beers with some very unique options.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (101, "Eric H", 5, "https://lh5.googleusercontent.com/-SjMk1saCH0M/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjV7BxxtNcPqMVX3FYrHOk0NkI4kFw/s128-c0x00000000-cc-rp-mo/photo.jpg", "A REAL GEM. We’ve been fortunate to enjoy many nice restaurants throughout the USA, Canada (especially Montreal), Europe especially France, Italy, Spain, and Finally Asia. Gary Danko ranks among some of the better and certainly is one of the nicer restaurants in the greater Bay Area. Do yourself a favor—book a reservation. You won’t be disappointed. Yet, Americans—don’t be in a hurry. Savor the food, wine and service. The service is spot on—I just sometimes see Americans in a rush and not enjoying dining. You’ll want to enjoy every second here. A real Gem. Thank you Gary Danko.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (101, "Eric Glover", 5, "https://lh3.googleusercontent.com/-OVTAzC6KCPc/AAAAAAAAAAI/AAAAAAAAAEE/eU5ehHWoawg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Amazing service and delicious food, worthy of their Michelin star. Every item I have tried was as beautiful to the eyes as it was pleasing to the pallet. In addition to their regular menu, they have a vegetarian special menu. For a Michelin star rated restaurant the price is quite reasonable. After the meal, they have a special dessert to go. Gary Danko is my favorite restaurant in the SF Bay area.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (101, "Jennifer M", 5, "https://lh5.googleusercontent.com/-bzVglnKMOx8/AAAAAAAAAAI/AAAAAAAAAHY/Ka2Az6IZJB4/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Amazing food and wonderful atmosphere. We went here to celebrate our marriage on our honeymoon and it did not disappoint. All of the food just melted in our mouths. The attention to the combination of flavours was great. The service was wonderful as well. We definitely recommend!", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (101, "Lee Braithwaite", 5, "https://lh5.googleusercontent.com/-wQhBmMjEIcA/AAAAAAAAAAI/AAAAAAAAAEU/hyS_ngxXgD4/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Food and service was spectacular! Highly recommended.... especially the Louisiana butter cake dessert", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (101, "Anantakumar LK", 4, "https://lh3.googleusercontent.com/-5SMvwACefHk/AAAAAAAAAAI/AAAAAAAABhU/vXOSTP1xjI4/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Good food and decent service. But for a one Michelin star restaurant, they do not stack up. Couple of times they spilled water on our table or dropped food. Two of the items in my four course menu was just average. It's not expensive so it is good value but I expected more from a Michelin star restaurant.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (102, "Julia L.", 5, "https://lh4.googleusercontent.com/-1UmRZpYX1t8/AAAAAAAAAAI/AAAAAAAAAgs/Xi19x7MMKJ4/s128-c0x00000000-cc-rp-mo/photo.jpg", "One of the best dining experiences of my life. Wonderful service, amazing flavors, delightful experience. We were seated at the bar which was surprisingly comfortable, and only ate off the dim sum style carts they brought around. We were full after sharing about 12 plates between two of us and sharing a half dessert- it was perfect! On a Thursday night in January we didn't have reservations so arrived about 50 minutes early and waited in line outside with a handful of others, crossing our fingers. It was actually a fun experience- watching through the window while they prepped. Obviously we got lucky and it was more than well worth the wait!!", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (102, "Tu Lam", 5, "https://lh5.googleusercontent.com/-Gsv7rI3H6J4/AAAAAAAAAAI/AAAAAAAA8Sw/gY4qds0XDQw/s128-c0x00000000-cc-rp-mo-ba6/photo.jpg", "I didn't come here for regular dinner service because it's really impossible to snag dinner reservations. I would love to come back here for an actual sit down dinner. My friend and I got tickets for their cook book release party which was basically easy whatever they're serving and add much as you like with 4 options for drinks (wine and sparkling wine). No formal tables add we walked around and grabbed food for a few tables or as staff walked by with trays. Every thing was delicious. I think we had about 8 different dishes that night.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (102, "Xavi Carb", 1, "https://lh3.googleusercontent.com/-wQFGA4KOEPM/AAAAAAAAAAI/AAAAAAAAAAo/SsLu-FnmDmI/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "We had booked a table for 4 and we had to wait around 40 minutes. The host did not manage the situation well and seemed stressed so much so that he left and as new diners came in they thought we were the hosts.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (102, "Jan Mico Villar", 5, "https://lh6.googleusercontent.com/-Ki7b3QNVpgw/AAAAAAAAAAI/AAAAAAAAARI/7paa8LJHy-c/s128-c0x00000000-cc-rp-mo/photo.jpg", "It is now my favorite restaurant in the city. The service is great very accommodating, and the concept is really appreciable. I had a very good impression about the food it was just superb!! It is a good spot for dates too. I think this is the most wonderful restaurant in San Francisco. As a tourist, I really recommend this restaurant to visitors it is worth of your time.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (102, "Derrick Wong", 4, "https://lh4.googleusercontent.com/-oAd0WhMx1KM/AAAAAAAAAAI/AAAAAAAAAAA/eXyS6gXOVBo/s128-c0x00000000-cc-rp-mo/photo.jpg", "This place is a great experience, an American-Asian fusion dim sum restaurant. The food here never disappoints if you are able to get a reservation. It may be a bit pricey for the little bites of food you receive, but keep in mind that they put in incredible amount of effort in getting the flavor and presentation just right. Highly recommend for those who are on the fence", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (103, "Matthew McDonald", 5, "https://lh4.googleusercontent.com/-vHiUZM7eOc4/AAAAAAAAAAI/AAAAAAAAACE/ti5OuMihpic/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "A hip and up and coming place, with a long history, that is great for an intimate date or a young crowd. It could be a very romantic place, but the atmosphere was more robust and energetic (as you would expect in an Irish pub). Our waitress was sociable and the meals a decent size. Some cool 80's music was playing in the background and noise from the TV's in the bar were audible; a pleasurable experience so much better than the typically dead atmospheres of other restaurants I've been in. A hidden gem in San Fransisco. 5 stars.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (103, "Jim Baird", 4, "https://lh6.googleusercontent.com/-i5NPi2Ha35I/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUIS5mbSrHw_AcM28eThbXJKZ3I9g/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Got to the bar and had prompt service. Good fish tacos, even with a minor mistake with my “hold the...” request. Probably a bit overpriced, but the atmosphere an Irish pub should have (well, an American urban version). Excellent bar staff. Worth visiting.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (103, "Melanie Paul", 5, "https://lh4.googleusercontent.com/-Vy59yNSshkc/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUr1DEHNhzRkJAU78M0oms7Ub-H7Q/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Lovely food and friendly staff. Tucked out of the way, but we'll worth a visit.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (103, "Shane Tepper", 4, "https://lh3.googleusercontent.com/-WdBtdT1CyuY/AAAAAAAAAAI/AAAAAAAAAvg/zBWElbsrUU4/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Food's pretty good (we got the beef stew and the bangers with beans and mash, both were tasty but served kinda...lukewarm). Beer selection is okay, a bit subpar for what you'd expect at an Irish pub. Decent selection of American, scotch, and Irish whiskies. Good place to sit outside when the weather's nice. Overall, a solid neighborhood pub.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (103, "Jessica Wallace", 5, "https://lh5.googleusercontent.com/-mAfUi8ZBpew/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWqP1Gfra99JnXj0ww0E8071Fmd0g/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great food with a traditional Irish Pub atmosphere. Great, quick service as well.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (104, "Stephen DeGuglielmo", 2, "https://lh3.googleusercontent.com/-4bxeaaHP66k/AAAAAAAAAAI/AAAAAAAAARA/1rxgJF652Eo/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Normally have a great experience at Auntie Anne's locations, but this one was a major disappointment. Not only were they out of the advertised pepperoni pretzel nuggets but they were out of the cheese dipping sauce. The only excuse to running out of one of your major dipping sauces is poor management. It's not like they have to make the cheese sauce. It doesn't even take that much room to store hundreds of the little containers. ", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (104, "Joseph Lovett", 4, "https://lh3.googleusercontent.com/-Mfn-P8ZGEo8/AAAAAAAAAAI/AAAAAAAAACE/TINHBrblMsM/s128-c0x00000000-cc-rp-mo/photo.jpg", "I LOVE the Almond pretzel and my son likes the mini dogs. The staff here is really friendly and I usually have to wait like 10 minutes but it is worth it to get a fresh out of the oven pretzel. We usually get the buy 3 get one free, stamp card ready! The drinks are pretty good too so I'm a happy camper.", "2 years ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (104, "David Be", 5, "https://lh3.googleusercontent.com/-BlzlvJUm8GQ/AAAAAAAAAAI/AAAAAAAAL0I/mbfK4w0xOXo/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Love it and staff is very cool. Got a pretzel for free by just telling how much I love their Pretzldogs. Wish I could have them in Germany, too.", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (104, "Jonathan Dybkjær", 5, "https://lh6.googleusercontent.com/-Acj1TAllMiI/AAAAAAAAAAI/AAAAAAAAChA/vj1_l50sTyA/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "We bought the original and the cinnamon bites. Apart from way too much sugar on the bites, they were both very good.", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (104, "Irene Chen", 5, "https://lh4.googleusercontent.com/-Y_i3g6SbPbs/AAAAAAAAAAI/AAAAAAABSGc/eNZeS0I8d-E/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "How they make the pretzels taste so good, I will never know. I suspect the secret has to do with butter and sugar.", "a year ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (105, "Raquel B", 4, "https://lh6.googleusercontent.com/-K7tb75ZQx_Y/AAAAAAAAAAI/AAAAAAAAHD4/h7iWowyLOg0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Seasonal produce inspires the French dishes. Best to make a reservation (particularly for the lunch crowd, it's a downtown favorite) Cozy atmosphere with lots of wood, marble, brick, and metal details. Staff is attentive and has a nice cocktail menu. Definitely, opt in for the dessert options. They're always delicious.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (105, "maureen lynn", 4, "https://lh3.googleusercontent.com/-JC0sjxPoZAM/AAAAAAAAAAI/AAAAAAAAAAc/0ax3uPHFTBQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "An amazing experience at this place. Monica and Arnie were our bartenders and really took care of us! Monica had a lot of great recommendations. Nelson was very friendly too and took care of us when the bartenders were busy! Definitely a great experience. Grab a Manhattan while you're there....you wont regret it!", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (105, "CELESTE MCCRAY", 5, "https://lh3.googleusercontent.com/-WDFqVGLTOUk/AAAAAAAAAAI/AAAAAAAACG8/oD--4d8P4EE/s128-c0x00000000-cc-rp-mo/photo.jpg", "It's a quaint restaurant that's dark, cozy, and intimate. The music playing was an extra. I ordered the Tom Collins and Lobster fries. The drink definitely hit the spot and the Lobster Fries were delicious. A must when in the Financial district! Didn't hurt my pockets either.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (105, "Erik Smith", 4, "https://lh3.googleusercontent.com/-gX4pg7CnSYE/AAAAAAAAAAI/AAAAAAAAb28/OwhzW40Ojaw/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Ammmaaazing food. Everyone was talking about what they got. I had the roasted chicken breast which was cooked very well and juicy. My friend next to me got the Sole Fillet and that tasted even better haha. The interior was definitely an eclectic mix of styles that somehow seem to mesh together. Only complaint was unfortunately my waitress. Super kind and friendly but she was not very attentive. Aka she poured more water in my cocktail before I could say nooooo. Took a while to come back and check on us. I'm assuming because they we're busy.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (105, "Sherman Wood", 5, "https://lh6.googleusercontent.com/-EwSL5w3yAAo/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUMk5XAoHG3mLHtwZZ3JS-OEaTBRg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Best bar and staff in the area. Not so loud you can't hear yourself", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (106, "melinda moy-elliott", 4, "https://lh5.googleusercontent.com/-QZHVtqDi3go/AAAAAAAAAAI/AAAAAAAAAtI/_-IwjEHkHpg/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Absolutely beautiful restaurant and pleasant atmosphere. The vibrancy is all around as you walk in...the colors, the service, the food, the drinks, the music. The food was delicious and filling. Bartender was very engaging and provided good drink recommendations. A place worth checking out for the full experience.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (106, "Phillips Roger", 5, "https://lh5.googleusercontent.com/-c9inYa6x2tQ/AAAAAAAAAAI/AAAAAAAAAAc/0IsOnYr9Yws/s128-c0x00000000-cc-rp-mo/photo.jpg", "Second time in a year we've come from Boise, Idaho and had the pleasure. Just being in their vibe makes me happy! Food and staff are superb. Had the chance to take our 16 year old, she adored it and the experience. Thank you and be back soon! Danny, Boise ID", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (106, "Jessie Pridemore", 4, "https://lh5.googleusercontent.com/-Kw72XY2dhk8/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUEr1UtYOcWxTn5szfCH3yajlesAw/s128-c0x00000000-cc-rp-mo/photo.jpg", "I went here for the first time with my friend. Restaurant looks really good from outside and it feels more simple when you get in. It is dark inside and you may think that its romantic bit it's really not because tables for two are next to each other. So literally you can hear what you neighbors are talking about. The food was really tasty and good. But its sooo over priced!", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (106, "Niraj Desai", 5, "https://lh5.googleusercontent.com/-hTkJhm89o9w/AAAAAAAAAAI/AAAAAAAAFl0/BU50i-cTVSI/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "I like to come here often because it is very difficult to find a Indian restaurant in bay area with clean and decent environment. Their food quality is very good as well. They also a complete bar which is an add on. They are good with South Indian dishes I would not recommend any north Indian items here...", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (106, "Marco Carvalho", 4, "https://lh6.googleusercontent.com/-Z8-ScCV6-H4/AAAAAAAAAAI/AAAAAAAAD5c/NZ8pAKKW3Sw/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great food with a lot of spices and fantastic flavor. Took one star out to reflect the dark ambiance, aka, need flashlight to read menu and see the food. I think it should be called a blind taste restaurant. A really good one.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (107, "Kristan Rose", 4, "https://lh3.googleusercontent.com/-XXsZocs6dXw/AAAAAAAAAAI/AAAAAAAAEcI/8HzXx2T442E/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "I recently revised my 3  rating to a 4  rating, as the customer service, quality of food, and cleanliness have all improved as of late. I'm sure there have been several new employees, and the senior staff have stepped up their game, considerably. Still, there's room for improvement, but all-in-all this is becoming a decent place for a meal at any time of the day ", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (107, "Milton Lau", 3, "https://lh3.googleusercontent.com/--i49F9DnTM4/AAAAAAAAAAI/AAAAAAAAAEw/mFpMKjQqu2g/s128-c0x00000000-cc-rp-mo-ba6/photo.jpg", "For this one particular Subway - it was better when the last girl was working here. But I don't think she works here no mo.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (107, "Donald MacPherson", 5, "https://lh6.googleusercontent.com/-AL41AXOu8aU/AAAAAAAAAAI/AAAAAAAAABg/XrSl9EEZSJ0/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Very quick service, sandwich of the day. Man I was hungry, delicious.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (107, "Mohamed Ahmed Ali", 5, "https://lh4.googleusercontent.com/-6bl7wpapfsU/AAAAAAAAAAI/AAAAAAAAAEY/6yut_YpQqY8/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Quick sandwichs", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (107, "Gemma Soyls", 4, "https://lh3.googleusercontent.com/-6J4QX1apN0k/AAAAAAAAAAI/AAAAAAAAYTI/LkOAjjjap18/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "It's an intimate place to eat and never busy. The counter intelligence is helpful without being gratuitously pushy to buy chips or a soda with a sandwich. The only drawback is the homeless people who come inside, rummage through the uneaten food inside the store. It was very off-putting.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (108, "Allison Vennerberg", 3, "https://lh5.googleusercontent.com/-IGWlq3gFhyg/AAAAAAAAAAI/AAAAAAAAHUU/oomDjnY_Otc/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "The food was good. The wait was absurd. I came here with my boyfriend for my birthday. We had a reservation and were seated immediately upon arrival. We put in drink orders and appetizer and entree orders shortly thereafter. The drinks came in a reasonable amount of time, as did the apps. We waited an hour and 45 minutes for entrees. The waiter stopped by several times to apologize and let us know the food would be ready soon, but that turned out to not be true. Nothing was comped for the long wait either. The food was good, but not good enough to wait at the table for nearly two hours. Highlights were the coconut rice and the cocktails.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (108, "Azul Ogazon", 5, "https://lh5.googleusercontent.com/-H7iep4V7M1s/AAAAAAAAAAI/AAAAAAAABvo/mXRGl_4qsVk/s128-c0x00000000-cc-rp-mo/photo.jpg", "One of the best places San Francisco has to offer! My friend and I ordered the Chef's sampler for appetizers, which included two different rolls, dumplings, tuna and pork ribs, all of them were very good, each with a special flavor. For main we had the lamb ribs and a side of fried rice; the lamb was so soft and the flavours were amazing! Finally we had the layer crepe cake, a bit sweet but with an espresso you have the perfect combination. Definitely a place that I would recommend and would love to visit again!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (108, "Alexander Ray", 5, "https://lh3.googleusercontent.com/-hAmikpLsZtU/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjW2wz4_Vms7OMj6_clnkK5HrsLWWg/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great place . It had a nice vibe. Good food. The restaurant is always full. The lounge upstairs is nice and relaxing. Not too crowded. Awesome personnel. Love to coming back to the place.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (108, "Paul Noronha", 5, "https://lh6.googleusercontent.com/-oSFkI2cWRrU/AAAAAAAAAAI/AAAAAAAAJYA/BV5EQ8PeuyU/s128-c0x00000000-cc-rp-mo/photo.jpg", "The name of this place hides the delicious Vietnamese haute cuisine offerings Le Colonial is known for. First class service, excellent, creatively prepared food, wine tasting, all wrapped up in what must have been the drawing room of a San Francisco men's club at one time. Don't let the decor deceive. Le Colonial is a great place to eat!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (108, "Heidi Kim", 4, "https://lh6.googleusercontent.com/-WaLauBMrRzU/AAAAAAAAAAI/AAAAAAAAKto/dhcILvBk78A/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Came here for a team dinner with my company. Michelle made it easy to organize and order for everyone, while be conscious of dietary restrictions. The food was delicious and the drinks were strong and tasty! Be aware: they have a dress code here!", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (109, "Luiz Centenaro", 3, "https://lh3.googleusercontent.com/-nmDHAGjKpqg/AAAAAAAAAAI/AAAAAAAAI70/zQ8CcH47Z7g/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "A sushi burrito. Pretty funky and unique experience. I had Geishas kiss and it was very filling but I prefer a more standard sushi experience. Overall it will cost about $12 for your meal and the line goes by quick!", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (109, "Paul Hung", 4, "https://lh6.googleusercontent.com/-GT6luAJPJmI/AAAAAAAAAAI/AAAAAAAAFgw/epBGxoQOzb0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Decor: 3/5
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (109, "Nando Sangenetto", 4, "https://lh4.googleusercontent.com/-RPl1vBDd544/AAAAAAAAAAI/AAAAAAAAABw/lza2o4TOZXs/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "It's a great place to buy some new kind of food. The mix of Mexico and Japan. You cannot sit here, you only can buy and leave.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (109, "Ryann Lee", 4, "https://lh5.googleusercontent.com/-SE7GAzH9B7I/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXg7EC5a0c3hEd9yy7kYr51H497nQ/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Love the food here. The line gets really long at lunchtime. Only open from 11-4 Monday-Friday. Closed on the weekend.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (109, "Eran Chetzroni", 5, "https://lh6.googleusercontent.com/-b3XlESRNB28/AAAAAAAAAAI/AAAAAAAANWE/dyy4B4pb2wI/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "The long lines at lunch are definitely worth it", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (110, "Gurnoor Dutt", 5, "https://lh5.googleusercontent.com/-2VdjasV3yHw/AAAAAAAAAAI/AAAAAAAAAA4/-JNMMnlB-Rc/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Best pizza place ever. They have fire oven baked thin crust pizzas, fresh toppings and delicious cocktails. Me and my friends love to come here. The location is so beautiful. Sit outside and enjoy the beautiful view of the water and the bay bridge. Good place for brunch/dinner", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (110, "Neil Minchin", 5, "https://lh4.googleusercontent.com/-nngXlSH8bUE/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUf9Dewo4x0oRLA2SKCsHtbOu8jGQ/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Great experience! Staff know how to earn tips. Great work Palomino. Would definitely recommend going here. Atmosphere, food & drinks... 5 star. Shout out for Jim. H for looking after us.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (110, "Tim Danser", 3, "https://lh5.googleusercontent.com/-SWCu3eJIGtE/AAAAAAAAAAI/AAAAAAAAAjI/ae1aqbh1SrI/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "The food was pretty good but service was sub par. If you had to rate this restaurant by food and atmosphere alone I think it'd receive a 4/5 star rating. However, when our group ordered drinks, the drinks came extremely late, the servers missed some people, and we had to keep reminding servers about our drinks. Overall though the atmosphere is really nice and good food.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (110, "Janet Nieves", 5, "https://lh4.googleusercontent.com/-T_f_Eqbf58o/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUDi50B9gNxYH-zj5ITnO12Pl6-fQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "I came here for lunch on a weekday with a large group of about 10 or so people. We made a reservation the morning of but it also wasn't very busy. There's a great view of the Bay bridge which you can see from their outdoor seating and some tables inside. There is a wide variety of choices on the menu so this was a perfect place to bring a large group. We were celebrating 2 birthdays and my engagement so they brought out 3 creme brulees which we all shared and everyone enjoyed very much!", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (110, "Sharmatha Devarajan", 5, "https://lh5.googleusercontent.com/-uGu2qxue7TQ/AAAAAAAAAAI/AAAAAAAAIBs/lCDRXzbRKsU/s128-c0x00000000-cc-rp-mo/photo.jpg", "Drinks are delicious and their happy hour is from 3 to close. So the perfect place to sit and chat as you watch the sunset. Also the gorgonzola fries are to die for. A definite must get.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (111, "Eric Paik", 5, "https://lh5.googleusercontent.com/-mm9zx-Bp6Pk/AAAAAAAAAAI/AAAAAAAA5oQ/YB0HL-rcVhs/s128-c0x00000000-cc-rp-mo/photo.jpg", "Fun atmosphere, friendly staff, great drinks and awesome nachos!", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (111, "Gemma Jordana", 5, "https://lh6.googleusercontent.com/-T9uvtShHPDc/AAAAAAAAAAI/AAAAAAAAAFA/1Jcpr1o_too/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great food and drinks here, for a really good price. Good atmosphere", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (111, "Daisy Planter", 1, "https://lh4.googleusercontent.com/-DO51T2VKLtw/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUKhwxS7Vup2As1fqpjWn6pvG7mQw/s128-c0x00000000-cc-rp-mo/photo.jpg", "I entered a contest online, a talent contest. 4 of us entered. The owner, the bartender and one other person. The other person didn't win, I didn't win, and the bartender didn't win either. That leaves the owner. How do you win your own contest???????????????
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (111, "M J", 3, "https://lh3.googleusercontent.com/-9jz65lzlsmg/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUUMWE0GZG6VnDjs-5ffL_3go2FEA/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Food is alright and service is inconsistent. Good music, but the red haired waitresses really put a damper on the mood for my table. We spent a little over 300 and the service was still lackluster ", "6 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (111, "Drew Foster", 5, "https://lh6.googleusercontent.com/-0gNz6xjOZw0/AAAAAAAAAAI/AAAAAAAAFEQ/4xTYeFn6_aM/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Great place to get lit, they have Froze` becareful as it tastes like a slurpy but has a serious adult effect ;-)", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (112, "Lerida Ramirez", 5, "https://lh6.googleusercontent.com/-PN1eLzDom9Q/AAAAAAAAAAI/AAAAAAAAAYw/oRxn4Fnq4EQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "Wow amazing food...the beef dumplings are the best...unique since dumplings are usually pork or shrimp...the sushi was great high quality ingredients...the best California roll ever! But the desert the warm chocolate with ice cream was to die for! Really recommend this restaurant it's worth the price. Didn't try the meat but can only imagine how good it must be. Finally one of the best service I have seen... food immediately removed when done from the table the waiter attentive to every last detail. Will definitely try this at other locations.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (112, "John Grey", 5, "https://lh5.googleusercontent.com/-v4YT3IFx6I8/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUTCS1bAUXhCIVLjwU30hiltBfbog/s128-c0x00000000-cc-rp-mo/photo.jpg", "The food and service was superb.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (112, "David L", 4, "https://lh5.googleusercontent.com/-bI3qy9i9Jiw/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUorhOzXxbipYUA6mlEUyniNmm0AQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Came here for the $65 restaurant week special in January. For the money, the restaurant week menu was worth it. Based on food alone, this isn’t the best place for sushi and isn’t the best place for steak, but if you should require both in one restaurant then this is probably your best bet. Prepare to spend $$$$ if you’re not coming during restaurant week. Also your clothes can somewhat stink after dinner because they have an open air kitchen.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (112, "Kenya Simmons", 4, "https://lh3.googleusercontent.com/-LyL6TEyvmh0/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWAOpG9dkTNfXC0Bt7jIXtY-vJg9w/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "A good place to eat sushi! Honestly, my first Telltale sign of a sushi restaurant is if I smell fish. If my Sushi smells fishy then I think it's old and I won't eat it. This place had fresh sushi and the ambience was quite nice as well!", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (112, "Ernie Bello", 5, "https://lh5.googleusercontent.com/-n3bnyogDzmM/AAAAAAAAAAI/AAAAAAAAAb0/6qUxQ9AVH98/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great sushi spot in the financial district. The employees are very friendly and good at their job. i like to sit at the sushi bar so i can watch Taku prepare my food. Total pro. The bar downstairs is pretty sweet too. Pop over to Kels next door for drinks afterwards.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (113, "Deanna Chase", 3, "https://lh5.googleusercontent.com/-7w8v5aMTB54/AAAAAAAAAAI/AAAAAAAAIKI/Z65FjevEAys/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "I had not been to Bottom of the Hill in years until last night 2/1/18. The Lillingtons were in town and I did not want to miss out on this! Doors at 8:30, show at 9. I arrived at 9:10 and the place was already packed. Friendly staff checking ID's and tickets. I did not order food or beverage while there, but saw others enjoying spirits and eats.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (113, "Lou Hampshire", 5, "https://lh4.googleusercontent.com/-sI_7gIpH5po/AAAAAAAAAAI/AAAAAAAAAFs/PHrsEogbLgg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great venue and staff. The most interesting performance bookings. Food was good, drinks were good. Atmosphere was great.", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (113, "Dillon Carlos", 5, "https://lh5.googleusercontent.com/-r-FUHu5LD0g/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVFYQsI9N8vlf-xfQsEs4vUirlBcQ/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "What a sick venue. Has a good bar with affordable beers for SF, and the window at the back of the stage is neat. It's a pretty small place, and the bands / performers there came out into the venue to hang out and sell merch before and after each set.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (113, "Abigail Jean", 3, "https://lh4.googleusercontent.com/-x4K_1o2w6I4/AAAAAAAAAAI/AAAAAAAAAJU/6GHWuGDbeYM/s128-c0x00000000-cc-rp-mo/photo.jpg", "Went to a concert here. The venue is pretty nice, but the bathrooms were discusting and the whole placd smelled like cigarettes", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (113, "Sarah Rosenberg", 5, "https://lh3.googleusercontent.com/-sm2QSiz6nco/AAAAAAAAAAI/AAAAAAAAAGE/IA9F5mhfseo/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Intimate yet lively venue with a cozy backroom and a cute secret courtyard (actually not so secret, as there is a window behind the stage looking out to it...more like hidden in plain sight)", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (114, "Jonathan Durand", 5, "https://lh3.googleusercontent.com/-UESKyFHekE8/AAAAAAAAAAI/AAAAAAAAOAY/S5x5ScRI7ao/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "This place is interesting how the waiters bring hot foods on the cart and bring it you on the table if you want them. Remember to carefully select the foods and check the prices. The food may look good but don't hurry to grab it Without knowing the price. Some food is small and pricy so look our for that. I hear many people complain how expensive on their bills not knowing the prices and ended paying it. But over all great service and the dining environment. I would come back again Recommended to reserve the table.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (114, "Irwin Kwong", 4, "https://lh6.googleusercontent.com/-G1RWEpjtOaw/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVlK6DTCWQLV8sXrYyBqcl6ndGSoQ/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Pretty good Dim Sum. Efficient and friendly service elevates the classic dim sum fare, while the occasional modern versions provide the memorable highlights. Points for their table side Peking Duck, it's not something you typically see at Dim Sum houses. Tight seating, cart service, and Quality teas add to the authenticity of the dinning experience.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (114, "Jamie Cohen", 2, "https://lh4.googleusercontent.com/-kMwcFFofIJs/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVPPB2N_3YzwIHifEU_eM8dIMBBWw/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "We were recommended to this place as a must try I'm torn between giving it a one or a two there was only 2 dishes that were average most dishes were below average and one looked inedible and half the dishes were room temperature or cold and considering how much it costs for an average at best dim sum also with below average service I'd never go back or reccomend it... the only 2 dishes I'd recommend was there pot stickers and there duck", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (114, "Tay Feder", 3, "https://lh4.googleusercontent.com/-WzoOAfCixGM/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXXc4cBzmUb8uRZMfRnyS6tgg1eyw/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Excellent dim sum but overpriced for what you get. The soup dumplings and shiitake were exceptional (5/5), and the other dishes were solid (4/5). We paid $45/person for brunch without drinks. If the venue had $$$ or $$$$ on Google maps’ profile instead of $$, I would rate 4 stars.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (114, "Kevin Jaskela", 5, "https://lh6.googleusercontent.com/-lUvwpMs7LIk/AAAAAAAAAAI/AAAAAAAAHUE/_fd_YUaEtBo/s128-c0x00000000-cc-rp-mo/photo.jpg", "Some of the best dim sum in SF. Always a lively crowd so make sure to come hungry but not starving, you might wait a bit.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (115, "Natalie L", 5, "https://lh6.googleusercontent.com/-Jba_G1219-I/AAAAAAAAAAI/AAAAAAAANCk/8RqzLm6Usgo/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great little punk bar that occasionally plays some good metal music as well. Friendly staff (hi Joe at the door!) and killer Bloody Mary's. And the burgers and grilled cheese sandwiches are great too. There's a beer garden space out back, a pool table in the room where you order food and lots of good local beer to order. But remember, they're CASH ONLY so hit an ATM on the way over.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (115, "John Overwine", 5, "https://lh3.googleusercontent.com/-6HsMQV4IMw4/AAAAAAAAAAI/AAAAAAAABN8/Or_JZhkKlvw/s128-c0x00000000-cc-rp-mo/photo.jpg", "Zeitgeist is an interesting punk/metal bar with a great draft beer selection, decent food, and plenty of outdoor space to enjoy the weather. Definitely a place to end the night, but don't cause a scene. They have a reputation for kicking annoying customers out!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (115, "Shawn Lucian", 5, "https://lh3.googleusercontent.com/-sFZifMWV1KM/AAAAAAAAAAI/AAAAAAAAHng/HzxWD5rPAjk/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "SF institution. Punk vibe and great backyard. When they fire up the q' make sure you get some. One of the top bloody's in the city, great mix and well dressed. Always a fan of Zeitgeist! PS - CASH ONLY", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (115, "Amber Zertuche", 5, "https://lh3.googleusercontent.com/-Bt8ebsvyGaw/AAAAAAAAAAI/AAAAAAAAF6U/fnc_3gA8MB4/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Many of my friends won't go here anymore because it is too busy but if you ever come on an off time it's such a great hang out spot with great music. I came on a weekday around 4pm and got my veggie burger and Roy Rogers right away. Love the outside patio.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (115, "James Wald", 4, "https://lh5.googleusercontent.com/-kXgImgdU9SM/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXjPXJJ5DXPCvbjEnHIphOEdepIMg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Nice place. Outdoor seating and good pinball. Somewhat of a hipster hangout. But still a great selection of local brews.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (116, "Carrie Brown", 5, "https://lh4.googleusercontent.com/-4ZqWwQu5md0/AAAAAAAAAAI/AAAAAAAAAl8/0c6ov55Owgw/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Dinner was perfection! Attendees we're on point, flowing around the table in an organized purposeful way. Restaurant did an excellent job of giving away just enough to make you feel a huge amount of value. The atmosphere feels like a swanky sophisticated neighborhood spot.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (116, "Justin Andrews", 5, "https://lh5.googleusercontent.com/-5b_VA0S_2aY/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWALJPid12mAXQ3fG_ZswNQFn0Zbg/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Everything here was excellent. The ambiance has a more modern feel to it. The Server was very friendly and recommended a very good wine. Most importantly the food was amazing and there was plenty of it. Normally with restaurants of this caliber the portions are very small and I go home hungry. Here they are on the smaller side as you would expect, but they throw in something in between every course so I was full before we event got to dessert. I would highly recommend.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (116, "Jimmy Chan", 5, "https://lh4.googleusercontent.com/-3OTdvt_U3no/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXfappvSO-w_Rt8otkV5XX07SSnLA/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Have only been here for restaurant week and it never disappoints. Upscale and superb service. The duck confit was moist and crispy. The burger was cooked to perfection, the English muffin seemed a bit out of proportion to the hamburger Patty. My favorite was the cauliflower soup, I couldve had 5 bowls of that. Definitely check this place out if you're in the area.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (116, "bethaney lee", 5, "https://lh6.googleusercontent.com/-shZgTQY4NYw/AAAAAAAAAAI/AAAAAAAAABM/orrmdoO_8Mw/s128-c0x00000000-cc-rp-mo/photo.jpg", "Talk about a five star experience. This restaurant is out of this world amazing. It lives up to its Michelin Star rating. Phenomenal bar selection and bartenders. Strongly recommend if you are looking to take someone somewhere impressive.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (116, "Mike Forbes", 5, "https://lh3.googleusercontent.com/-nhxCRUaLS6g/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWNg-X_DumX_636XQoZiCGw14vvCg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great restaurant! Great cocktails, wine and spirits. Amazing food. Excellent atmosphere. Great recommendations by staff as well.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (117, "Christopher Lee", 3, "https://lh5.googleusercontent.com/-txr0imt_ZOY/AAAAAAAAAAI/AAAAAAAAV2c/oByOQJvoqcY/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "It’s a nice, well lit restaurant with a nice ambiance to it. The starting chips are excellent, though the salsa is a bit thin. Service to start is fast, and chips and drinks are refilled quickly. You also get a tall decanter of chilled water at seated tables. As for the food: the meats we had were excellent. Vegetables were alright. The tortillas we found were very dry and floury, which took away a lot of the flavor. The menu is well labeled for gluten free options and there does not seem to be cross contamination in marked foods (though there is no flag or different plate color for gluten free orders). If this review is updated within 24hrs then you will know the food is not celiac safe! It’s rather pricey to dine here for the quality of food. Not a bad option, but would not be my first pick.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (117, "Raquel B", 4, "https://lh6.googleusercontent.com/-K7tb75ZQx_Y/AAAAAAAAAAI/AAAAAAAAHD4/h7iWowyLOg0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Everything on the menu is delicious! Love the guacamole that is prepared fresh at the table. Nice tequila selection. Comfortable seats. Has a nice patio that gets sunshine earlier in the day. Great spot for lunch or dinner and happy hour too.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (117, "James Brogan", 4, "https://lh5.googleusercontent.com/-bjCM3fmbtbc/AAAAAAAAAAI/AAAAAAAAIRk/EU4xRkmph8s/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Amazing upscale Mexican. I still like taco trucks on the flipside. But some very creative, flavorful and colorful dishes. Great place. A tad pricey but worth the experience.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (117, "Ashley Baer", 3, "https://lh4.googleusercontent.com/-Qba4rfx-o0Y/AAAAAAAAAAI/AAAAAAAAAB4/vpbAS4g_K6w/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "The food was good, but perhaps a little upscale or authentic for my tastes. I think I'm so used to super greasy and cheesy Mexican food that anything that's NOT that is too different for me. The food here was fresher, cleaner, and a little under-seasoned. I wanted more oomph. Also, the service, while professional and attentive, was slow, and I think this had to do with the food more than anything. Don't expect a quick meal here.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (117, "Lena Robinson", 3, "https://lh3.googleusercontent.com/-isPSIhK7zfw/AAAAAAAAAAI/AAAAAAAABtk/CvfuInp0BA8/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Good food but pricey. Entrees were cold when we finally got them. Very busy with holiday lunch parties so service was very slow. I love the ambiance. Decor is festive and elegant. Staff were nice.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (118, "Davy Lefkowitz", 5, "https://lh5.googleusercontent.com/-mpvB3gypgI0/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVF3B899oiUY0eBmtsrlZEGCsyYeA/s128-c0x00000000-cc-rp-mo/photo.jpg", "Top of the line bar/restaurant! The food was delicious and the customer service was even better! Extremely professional and friendly! I don't go out to fancy places like this often but the people made it a great experience!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (118, "Thor Johnson", 5, "https://lh3.googleusercontent.com/-rgjgi6a9jfk/AAAAAAAAAAI/AAAAAAAACco/WO21Y5bIt8Y/s128-c0x00000000-cc-rp-mo/photo.jpg", "First rate sushi restaurant. Choices abound, from 'simple' sashimi to the most exotic. Perfect service, too. They handled our large group with ease.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (118, "Todd Hackett", 5, "https://lh5.googleusercontent.com/-hYgjsA-ofRY/AAAAAAAAAAI/AAAAAAAAB_U/b85Nn5aJ_sE/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Though pricy, the ambience, quality of service and availability of valet services make up for it. The food? Top notch, high grade and aesthetically pleasing. Might I add that the bathrooms are in pristine condition.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (118, "Gary P", 4, "https://lh6.googleusercontent.com/-LBjpYePbt38/AAAAAAAAAAI/AAAAAAAAIcg/BwFLCt704IQ/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Great view of the Bay Bridge at night. Nice vibe. Great eats.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (118, "Janet Nieves", 4, "https://lh4.googleusercontent.com/-T_f_Eqbf58o/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUDi50B9gNxYH-zj5ITnO12Pl6-fQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great place for lunch! I ordered the Kaisen-don which is prawns, scallops and salmon over rice. The portion of rice was very big compared to the amount of seafood and in the end I still had half a plate of rice left. I would have liked the ratio to be a bit better, let's be honest we mainly want the seafood, but it is a rice bowl so can't really complain. Overall it was very filling and delicious", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (119, "Robert Shopshear", 4, "https://lh3.googleusercontent.com/-OGu63towzTw/AAAAAAAAAAI/AAAAAAAADLM/FkulOCiO0Wk/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "First time at the Mikkeller bar and it was a weekend. They started serving Brunch recently and if you get a chance try the corn beef and hash. This is no store bought corn beef and it was fantastic. It went well with the solid beer selection. My only gripe is the price of the beers. They do have a good amount of one off and unique beers but the price tends to be a bit higher than most places.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (119, "Marc Hill", 5, "https://lh3.googleusercontent.com/-jW-OGY62iHM/AAAAAAAAAAI/AAAAAAAAHwk/E2Dd4O9lgfc/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "A modern twist on a traditional German beer hall with an overwhelming choice of flavourful beers to choose from. The bratwurst is moist and delicious served with plentiful mustard and sauerkraut. Cheerful casual waiters make sure your glass never goes empty and share their knowledge liberally.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (119, "Renaud Perrier", 5, "https://lh3.googleusercontent.com/-cAGl9LcWoXY/AAAAAAAAAAI/AAAAAAAAycY/eQ-fkPVJCds/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Superb atmosphere, a very nice restaurant. Eat at the majestic bar, wonderful. I tasted the pepper steak: really amazing! Some really good bites, lot of good drinks. I will go there for lunch, for after work and drinks, but maybe not for dinner (noisy, full)
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (119, "Joe Sassaman", 5, "https://lh5.googleusercontent.com/-YO94zE0Kj7w/AAAAAAAAAAI/AAAAAAAAGSQ/Pu-wvsYQ7AU/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great beer selection. Tasty food. Did I mention the variety of beers on tap? And not just a bunch of crappy beer but amazing beers served properly. This is a beer lovers bar. Yes the neighborhood is still a little sketchy but that makes this place legit.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (119, "Mark Litman", 5, "https://lh4.googleusercontent.com/-m2ygXGNUCx4/AAAAAAAAAAI/AAAAAAAAGoc/_XiXbFLfkhA/s128-c0x00000000-cc-rp-mo/photo.jpg", "One of the best bars I've been to in SF. The beer choice is very wide and changes often. The food is really nice too. Gets a bit noisy at certain hours but with voices of people talking, definitely not loud music. I loved visiting this place at odd hours, sit at the bar and have conversations with strangers.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (120, "Pranam N Shankar", 5, "https://lh4.googleusercontent.com/-K9ZY5Mn0vVQ/AAAAAAAAAAI/AAAAAAAAEcI/74LKxmnqO-E/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great Italian place with plenty of seating. Went there for the team lunch. They were very accommodating and friendly. The cappuccino and espressos are really good", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (120, "Kowonda Perkins tyrell", 5, "https://lh3.googleusercontent.com/-eYIgIFhYSxc/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXboChTNoFO43JfzFWaNO0nNKLpGg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Very upscale spot. Well worth the prices. My server had an issue with me. I didn't let that ruine my evening. He was servicing me unexpectedly. I enjoyed watchingg him try n figure me out as his face was filled with question...not unquiry!!!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (120, "Michelle Chang", 4, "https://lh3.googleusercontent.com/-8itdJP3-0mY/AAAAAAAAAAI/AAAAAAAB7EE/A1ZI2kavVeY/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great service even for our group of 15+. Caesar salad a bit too citrusy for my taste.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (120, "Toby Masson", 4, "https://lh6.googleusercontent.com/-z8JOICy8V6s/AAAAAAAAAAI/AAAAAAAAAKY/qBYBjWT5Z6s/s128-c0x00000000-cc-rp-mo/photo.jpg", "A great little spot that offered some welcome relief from my studies at Hult Business School, which is just across the street. The bar staff were friendly and accommodating, pizza was delicious and the Golden Gate IPA kept me coming back for more. The only reason that it didn’t get 5 stars is the closing time. 10:30pm is early even by an Englishman’s standards.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (120, "Johann C. Frey", 5, "https://lh3.googleusercontent.com/-O4Rdd4aMQMg/AAAAAAAAAAI/AAAAAAAAS6E/5jXSzSvMXhs/s128-c0x00000000-cc-rp-mo/photo.jpg", "Still the best place on Sansome. Wonderful ambiance, great food and coffee", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (121, "Irene Personal", 5, "https://lh4.googleusercontent.com/-P3HM54lomqA/AAAAAAAAAAI/AAAAAAAAFTI/BDMzMLMijV0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "You can be sure you're going to be taken care of as it's a family owned traditional style, yet classy and special experience dinning at Waterfront. Service is friendly, attentive. Staff know the menu and can make recommendations or take care of special orders and preparations if you're particular or are allergic to any food items.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (121, "Cute Dragon", 5, "https://lh4.googleusercontent.com/-raC6eer0drk/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjX5Q5Ndz5r086-O9aI5A-ztWxvGKg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Best wonton soup ever. Valentine's service was excellent They helped us work out Uber for the first time. Great wine. We sat at the bar after dinner. Nice view. Very romantic.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (121, "Nirushan Udayakumar", 4, "https://lh4.googleusercontent.com/-HfpLlqkpDYs/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVirI0ZO3asopyDyWIor1NnFIqZng/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "This restaurant is located by the water so you have an amazing view while you dine. The food, and seafood in particular, is all really good, however the portions are really quite small. Even after an entrée, drink and dessert, I was still left hungry", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (121, "Chad Allen", 5, "https://lh5.googleusercontent.com/-2JNbWtxBQdA/AAAAAAAAAAI/AAAAAAAAJN8/BBO3mt2_2io/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great view with an extremely friendly staff! The seafood appetizer, nutella dessert and Paloma drink were a must! I highly recommended this restaurant.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (121, "Delphine Bernard", 3, "https://lh6.googleusercontent.com/-ZWxV0g-o6Q4/AAAAAAAAAAI/AAAAAAAAABE/bIcKrsDL6vs/s128-c0x00000000-cc-rp-mo/photo.jpg", "Amazing view
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (122, "Chris Garnica", 4, "https://lh5.googleusercontent.com/-atdSLmkDCo0/AAAAAAAAAAI/AAAAAAAAAD4/McR9fDt9E3w/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Went here for brunch and ordered the sassy bacon and smoked salmon omelet, both were incredible. The bacon is super thick, sweet and spicy, a must-try for bacon lovers. Then there was the omelet which was loaded to the max with quality smoked salmon. There was also the bottomless mimosas with a ton of different juice flavors, which I didn't go that route but they looked good. The venue itself isn't all that attractive, certainly not for brunch, but the tasty menu items and lively vibe make up for it.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (122, "Shaprieh Bean", 4, "https://lh5.googleusercontent.com/-_Loi4FsySYI/AAAAAAAAAAI/AAAAAAAAaP4/skVeWfYDUrE/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Great music, food was delicious and the bottomless mimosas were everything. I had the smoked salmon omelet which came with a side salad and potatoes. More than generous sized plate for one. Definitely could have split it with someone and been fine. The music was lit (trap rap most of the time I was there which was perfect for the turn up).
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (122, "Adam Aquino", 4, "https://lh5.googleusercontent.com/-eB9VMvUafK4/AAAAAAAAAAI/AAAAAAAAAAA/YoEIwvIX3Ng/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Don't go to bars too often but this place was cool. They could tone it down on the cleaner like anonia cause it was really strong. Most people can't notice cause their getting drunk", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (122, "Colin Lang", 3, "https://lh4.googleusercontent.com/-nGoWHUHJRpo/AAAAAAAAAAI/AAAAAAAACzg/LR4TSWTyGVU/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "The food is good, I througly enjoyed my brunch. The staff was also really sweet. However, there are too many people for the staff to handle so it's hard to catch their attention. Also if you like bringing the club to brunch and not being able to hear and talk to any of your friends while eating, this is the bumpin' place for you.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (122, "Briana Gutierrez", 5, "https://lh5.googleusercontent.com/-AXA1ccYgSao/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXj6E0YpjXuaHuyMgoqn5EDZQt6zg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Best place ever! We're from southern California and my group and I were recommended this place! So fuu, so lively, and our waitress Ashley absolutely amazing!!! So honestly, she knew menus suggestions, and she knew great drinks!!!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (123, "Keaton Covillo", 5, "https://lh5.googleusercontent.com/-BQKiivoYRX8/AAAAAAAAAAI/AAAAAAABKnQ/hiTs_pyCTgY/s128-c0x00000000-cc-rp-mo/photo.jpg", "This is a local favorite, it’s the best way to spend an afternoon, soaking up rays and drinking beer. They have a large selection of beer, and their snacks are awesome too. I would suggest their pickled assortment or their infamous pretzel.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (123, "Aleksandr T", 4, "https://lh4.googleusercontent.com/-jaa2sx3dXns/AAAAAAAAAAI/AAAAAAAAAEw/2EO8ncyL_90/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Really long lines. The place is awesome but be prepared to order all your drinks at once.. otherwise you're waiting another hour for a beer.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (123, "Varun Sood", 5, "https://lh6.googleusercontent.com/-7JmuBwJo6dY/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjV4BLRpi2OOu_ykdu9FD-k3zRb5nA/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Awesome outdoors beer garden with lots of tables and beer by the litre. You even get to keep your mug!! When we visited there was a petting zoo setup nearby which made it even more fun. Love this spot.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (123, "Richard Lee", 4, "https://lh5.googleusercontent.com/-e3ox49vs5eo/AAAAAAAAAAI/AAAAAAAAAHM/n-IRDYWfyWk/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Liters of beer! In a concrete lot, so not exactly Munich, but fun place to observe Oktoberfest (or a Friday).", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (123, "Elizabeth McDonald", 5, "https://lh4.googleusercontent.com/-UNgOmvrGkWE/AAAAAAAAAAI/AAAAAAAAAAA/D185Roispjo/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Pleasant outdoor space with tasty beer and German food (sausages, large pretzels, etc). Can be somewhat crowded but I've never been unable to find room. Babies are permitted.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (124, "darren parker", 2, "https://lh4.googleusercontent.com/-iQPCcg5HPas/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVt0ZnArbSgVekaim4OWg4egPhlGw/s128-c0x00000000-cc-rp-mo/photo.jpg", "Ate here whilst staying in San Francisco but very disappointed. As a big fan of steaks, I've tried many across the years, in many restaurants in different countries and in all honesty I've had a lot better for a lot less money!! Very, very overpriced. Why? Dimly lit restaurant that makes it very hard to read menus. Steak was very dry which was a shame as it was a good cut, just badly cooked that rendered it devoid of all succulence. Then they only allow you to share potato and side dishes which may work out well for a number of you but when there's only two people and you don't like or want the same choices it bumps the price up to actually have what you want. They refused to do half potato half veg option! Why?? They're cooking the stuff anyway- a good restaurant would allow you to do that!! Had to order a share portion side of potatoes and veg which wasted a lot and everything was way too salty!! Adding salt to everything does not make it better! Cooking it to retain its natural flavours makes it good. I actually felt guilty afterwards for wasting so much money (over 200 dollars on one main for two people) for a distinctly average chain store steak. Won't be visiting again", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (124, "Robert Nickell", 5, "https://lh4.googleusercontent.com/-jF9dgHeNBh4/AAAAAAAAAAI/AAAAAAAABTI/QUTcymuV2t4/s128-c0x00000000-cc-rp-mo/photo.jpg", "Wonderful food and atmosphere. Reservations are a must! Service was outstanding. Be prepared to pay out some $$$$$$. WELL WORTH IT.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (124, "sydney dawson", 2, "https://lh4.googleusercontent.com/-kagVRc5iu50/AAAAAAAAAAI/AAAAAAAAAA8/mHqgaJN_eTU/s128-c0x00000000-cc-rp-mo/photo.jpg", "Good food and good service. One thing that is the downside is one of their neighboring businesses RocksBox. Yeah they suck.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (124, "Warren Gatto", 1, "https://lh5.googleusercontent.com/-kTh2t8OEmDM/AAAAAAAAAAI/AAAAAAAAAo4/0tzbpjE82J8/s128-c0x00000000-cc-rp-mo/photo.jpg", "Highly overpriced (even by NY standards). While the steak was good, they do not know how to cook it. There was no crust or char on the outside. I looked at the kitchen (it is an open air kitchen) and see they cook the steaks (as well as other food at the same time) in a broiler which is about 12 under the flame. Not the way to do it. If you want a good steak, there is only one place - Peter Luger's in NY. There are other steak houses in San Francisco which offer a better steak. This was a huge disappointment.", "6 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (124, "Guy Rosen", 5, "https://lh4.googleusercontent.com/-zuWQUCWzDTk/AAAAAAAAAAI/AAAAAAAASko/PGCsJhjOGYA/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "The perfect storm. Great food and great service on the bar at this classy food and bar restaurant. Was there twice in the week, eating a few of the bar menu, reasonably priced and cooked to perfection, both the set of 3 steak fillers or the trip of burger sliders", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (125, "Stefan Hofer", 4, "https://lh5.googleusercontent.com/-zIBsWTrdzN8/AAAAAAAAAAI/AAAAAAAACHA/P0QlAIypo9E/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Nice fish restaurant in down-town San Francisco, hidden in a little side street. A wide choice of fish and seafood from fresh oysters in different qualities and styles to simple fish and chips. The service has been great but I would consider it pricy. For a good table discussion probably too noisy but you can also sit at the bar and eat. Worth a visit...", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (125, "melinda moy-elliott", 4, "https://lh5.googleusercontent.com/-QZHVtqDi3go/AAAAAAAAAAI/AAAAAAAAAtI/_-IwjEHkHpg/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "A good seafood (mostly) spot in an off the main Street location. The place has high ceilings and is airy, which makes you almost feel like you're in a fish market. The wine pairings are suited for fish (white or rose) and go nicely with the food offerings. Mussels, chowder, oysters are all solid. Recommend for those who don't want to go to fisherman's wharf for some good seafood.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (125, "Josh Longnecker", 4, "https://lh5.googleusercontent.com/-ZksoNVlO7tw/AAAAAAAAAAI/AAAAAAAAAKM/jYApWaNB8WE/s128-c0x00000000-cc-rp-mo/photo.jpg", "Food and drinks were great but it was a little pricey for the appetizers. Good spot for a happy hour", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (125, "Mike Wilson", 2, "https://lh3.googleusercontent.com/-gmO1HRKA6f4/AAAAAAAAAAI/AAAAAAAAAFw/YR8_a5-mQTk/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "I have been here many times and usually the experience has been fantastic. This time was terrible. Our server Casper was one of the worst servers I've ever had in my entire history of dinning out. She was terrible. Our large party (7 people) was treated poorly. Food was tasty as always but the service experience was bad enough for me to write this review. Sorry to see such an excellent restaurant ruined by such a terrible service experience....avoid until they correct the staff issue. ", "6 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (125, "Lars W", 5, "https://lh5.googleusercontent.com/-zvjcr0HXuNU/AAAAAAAAAAI/AAAAAAAADG0/QZoIYL7JtO8/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Friendly modern design. Result good bar staff and healthy food options. They also have a decent craft beer and import beer selection.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (126, "Andrew Jaru", 5, "https://lh5.googleusercontent.com/-MCY2yswvYJo/AAAAAAAAAAI/AAAAAAAABBc/c_UVIvcCefE/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Had a great experience here. Everything was on point. Food came out in a timely manner, service was always a step ahead of you, and the ambience was invigorating. The dishes themselves were bustling with flavor. For an appetizer, I had the cornbread which had pockets of rich, salty flavor. Followed my the beef duo for my entree. It was rich and tender with a sampling of different flavors. My date had the buttermilk chicken and waffles which was nice, but not the best thing on the menu. Overall, a great place for a special occasion.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (126, "Margaret Lee", 5, "https://lh4.googleusercontent.com/-YzljV3yV7pY/AAAAAAAAAAI/AAAAAAAAYzM/D2kZzVBqkl0/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Get the fried chicken and just know it's an obscene portion. Split it, get a doggie bag, or just own it. So good. Lickin' lips for days.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (126, "Jedediah H", 5, "https://lh6.googleusercontent.com/-Is52KKB22f4/AAAAAAAAAAI/AAAAAAAABhg/CKvhcqylGXQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "Pricey place, but great food and service. Worth every penny.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (126, "Tony Johnson", 4, "https://lh4.googleusercontent.com/-ftdsSgpCqOs/AAAAAAAAAAI/AAAAAAAAAg0/amY7w7SRIkg/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Food is always excellent. Great service as well.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (126, "Xavier Jimenez", 5, "https://lh6.googleusercontent.com/-RmN21_c0Ebw/AAAAAAAAAAI/AAAAAAAAC5o/8yDx9Q-VxsY/s128-c0x00000000-cc-rp-mo/photo.jpg", "great food and good vibe for work related get togethers", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (127, "Kenneth Wong", 4, "https://lh3.googleusercontent.com/-JRaTDC6lqEQ/AAAAAAAAAAI/AAAAAAAACB0/PUUJSBQewrg/s128-c0x00000000-cc-rp-mo-ba6/photo.jpg", "I've eaten here many times. Usually, the broth and noodles are very good. Today, had the vegetable ramen with pork broth. It hit the spot. In general, food is consistently very good and service is fast and friendly.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (127, "Andres Romero", 5, "https://lh5.googleusercontent.com/-QVOwG0gHoV4/AAAAAAAAAAI/AAAAAAAAADM/MQJ_yoOWeE0/s128-c0x00000000-cc-rp-mo/photo.jpg", "I bought the fried chicken ramen and kiwi slushee, the ramen is the best I've ever had and the slushee is so good if I could I would buy all the slushees they have.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (127, "Lucio Rafael", 4, "https://lh5.googleusercontent.com/-Mm9mVC2Jn3A/AAAAAAAAAAI/AAAAAAAAEoY/0IJmyaVczMg/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "With dishes around $10 it´s good value for money. Speedy and cordial staff. There was a small line (around 10 people) that was attended very quick (they have that manual waiting list on paper). Groups of 2 or 4 is recommend to be seated fast. Boba Tea Milk is a interesting choice if you like tea in general. Good surprise.", "11 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (127, "Gabriel Staples", 5, "https://lh6.googleusercontent.com/--XCGaBkR4Ho/AAAAAAAAAAI/AAAAAAAACxI/Hr7HDIosKYo/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great food; decent price; good service.", "5 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (127, "P Su", 1, "https://lh5.googleusercontent.com/-ZwyXz7otg-8/AAAAAAAAAAI/AAAAAAAAF8A/S2sLBugWCgs/s128-c0x00000000-cc-rp-mo/photo.jpg", "Ordered a kiwi sushi and pork ramen for 14. Kiwi slush was mostly large chunks of ice. The ramen had an oily soup base with very little pork flavor. The ramen were pasta noodles. ", "5 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (128, "Simon Donovan", 4, "https://lh6.googleusercontent.com/-BGGVcDgZVcI/AAAAAAAAAAI/AAAAAAAAADU/aGNsFZsfnYg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Great place for a quick drink after work and the appetizers with the drinks are very nice. They make a great Moscow Mule in a copper cup which I always love. I ain't your once with the wife and we really enjoyed it it's a long way to come downtown. I would say great drink place after work and dinner with friends and or work colleagues.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (128, "Nicolas Celle", 5, "https://lh5.googleusercontent.com/-EwFH-GQXch0/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXE9ZKCZzxw-LeGCHygi-BLYcqDag/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great food. Best duck I had. My colleagues also raved about the king salmon.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (128, "Elizabeth Rice Ramirez", 4, "https://lh4.googleusercontent.com/-xYEh6bNL8ZE/AAAAAAAAAAI/AAAAAAAAAW4/9zJTJb-5Tkg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Food was really good, but the menu was limited. If you didn't want any of the several selections, you'd be out of luck, but what they served was delicious. The desserts were super creative.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (128, "James Hughes", 5, "https://lh4.googleusercontent.com/-niI0DSXgJSk/AAAAAAAAAAI/AAAAAAAAqlU/hCp94nAszho/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "One of my favorite restaurants in San Francisco, great happy small bites and exclent drinks or wine. Friendly people and great staff. Deffentley worth the stop. I don't give 5 stars often. You be the judge..", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (128, "Renée DuPree", 4, "https://lh4.googleusercontent.com/-w1vg7jcFfpk/AAAAAAAAAAI/AAAAAAAATBg/6M2TGHtV6FM/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "I've been to prospect tons of times for happy hour. Their specials are great, the snacks are delish, and if you come early enough, finding a seat isn't hard. I came for lunch the other day and was a little surprised to see that they have no vegetarian entrees on their lunch menu. It was a little bummer, but I was able to cobble together some starters to make a meal. Definitely swing by for happy hour, but if you're a vegetarian, you may want to skip lunch.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (129, "agenor de lima vianna", 1, "https://lh5.googleusercontent.com/-kf3Uo65enUg/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXhoARPvXOAU39G7lYgrSjfWD4dKQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "Although the meat is good, they charged more than they announced for the rodízio . My 6 year old boy paid like an adult. The price of very small coke is very expensive too. I felt like I was fooled.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (129, "ashlei jones", 1, "https://lh5.googleusercontent.com/-tqRffrS1Jds/AAAAAAAAAAI/AAAAAAAAAB4/cYyDjjTF-j8/s128-c0x00000000-cc-rp-mo/photo.jpg", "It’s vday, so I understand it’s busy, but you can’t just create random tables and put them in walkways and awkward places which cause the entire dinner to be enjoyable and if you plan to seat someone in these areas, probably makes more sense to ask them first if they are ok with it", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (129, "J", 4, "https://lh3.googleusercontent.com/-urSJo7W-dOU/AAAAAAAAAAI/AAAAAAAABNM/5HPh4k0N6XU/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Delicious meat for days. They keep coming back with different options (including shrimp) unless you say you're good. In addition to that, they have a great salad bar. You will not leave hungry. I'm not an expert but the wine selection seemed ok and they had beer and mixed drinks.", "5 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (129, "Eric Crane", 5, "https://lh5.googleusercontent.com/-SwS-NcXoZgk/AAAAAAAAAAI/AAAAAAAAAEU/T8E-1mLcJ-Y/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Plan to eat for hours. When I go here I plan ahead and eat as little as I can for a day or so before I go. The service is continuous - they keep bringing food until you surrender and turn the little indicator wheel from green (bring more meat) to red (stop - I'm going to explode). There is a salad bar that is stocked with fantastic food but the reason you should go is the meats. They keep bringing the best cuts of meat for as long as you can keep eating. Pace yourself. This is a marathon; not a sprint. Take your time and set a pace you can maintain or you will miss out..... and or die from eating too fast.", "7 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (129, "B Williams", 5, "https://lh5.googleusercontent.com/-jsU9m0QpZ84/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXNbdTHXqAd1mdMkC39cOTBYDrCXQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Go here hungry and expect to leave full. It was an amazing experience of lots of different meats. Nice salad bar as well.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (130, "Justin Schauer", 5, "https://lh4.googleusercontent.com/-tVntsEcOazk/AAAAAAAAAAI/AAAAAAAAAAA/cW3oa6BM_h4/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "We were immediately seated for our reservation and the service was good. We ate absolutely everything, practically licking the plates clean. It was delicious and the perfect amount of food. We shared the bacon bread and green salad and then each got an entree. The pork chop is breaded, which was unexpected but delicious. Very cozy place. The interior is very similar to Tunnel Top, which is next door.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (130, "Thomas Horsten", 5, "https://lh5.googleusercontent.com/-q5bk2fuhq2A/AAAAAAAAAAI/AAAAAAAAG_g/kysyywTiWzs/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Excellent bit of France in SF. Great service, had our own mini wine tasting when choosing the red to go with our steaks. The wine list has an excellent choice of reasonably prices wines. The tuna starter and the steaks were cooked perfectly and everything was well presented and very tasty. Atmosphere on cozy top floor dining area just perfect for a romantic evening. An excellent night out, wouldn't hesitate to recommend.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (130, "Darcy Dittrich", 4, "https://lh3.googleusercontent.com/-4-cD_ndhk2A/AAAAAAAAAAI/AAAAAAAAAZQ/fjuF1y4HPds/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Delicieux. Feels like you’ve discovered a little bistro in Paris. Dark, moody lighting with seats at the bar, main seating upstairs. Reservations are definitely recommended. Service is great and the food is outstanding! I had the salmon which was medium rare with perfectly crispy salmon. We also ordered the crispy brussel sprouts (amazing), lamb appetizer (succulent). My boyfriend had the bistro steak and loved it. The only reasons I’m not giving 5 stars is the menu is a little limited, and it’s a bit pricey. That being said, everything they make is perfectly executed, and I love bringing friends here for special occasions.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (130, "K D", 5, "https://lh6.googleusercontent.com/-fomlu1XU_Dc/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUFVArNRb1jq39PcPAERx7qPksUVQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "I've dined here twice and it was excellent both times. They were very accommodating with regards to our theater reservations and made sure we were able to fit in dessert. I really like the wine recommendations from the wait staff, can't wait to go back!", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (130, "Douglas Goldblum", 5, "https://lh4.googleusercontent.com/-ZqA1Y43Kp_Q/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjWIjPQkonQqg8vLrouBDUqKTH_MTw/s128-c0x00000000-cc-rp-mo/photo.jpg", "Excellent French food. Funky, casual, cool atmosphere. Definitely recommend.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (131, "paul ledingham", 5, "https://lh4.googleusercontent.com/-_-9R3gQU2Uc/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjX434iL1ebFUuZjkK0cZnnppqzZog/s128-c0x00000000-cc-rp-mo/photo.jpg", "Simply put, we loved this place. Perfect for a lazy sunny afternoon lunch and beer. We sat out the back on the terrace, over looking the water. I had the Longshoreman's Special (American sized Chowder I was informed) and the wife had the Crab and Shrimp Louie Salad. Both were tasty, good, hearty meals. They had a good and varied selection of beverages, but what really stood out were the serving staff. I believe it was Tony who served us predominately, but they were all lovely. He noticed I had almost finished my beer, and offered to have one ready and waiting in 5 minutes for me. Perfect. As an afternoon retreat away for the hustle and bustle of the main town it is a great choice and for me that's why it gets a 5. One question though. What is the deal with that heater?!?!", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (131, "Jed Brian", 1, "https://lh6.googleusercontent.com/-B_juKKDwoNM/AAAAAAAAAAI/AAAAAAAAApg/vWyFUnxnFDw/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Warning - Our credit card information was stolen from this restaurant. Shortly after we left this restaurant we were contacted by the credit card company and notified about suspicious purchases. This card hadn't been used for any other purchases for weeks before and nothing after. I called the restaurant and was promised a call back...that was months ago and I'm still waiting. Otherwise the food is just okay. 3 stars at best.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (131, "Alberto Frigerio", 5, "https://lh5.googleusercontent.com/-ethAM7p_Zbc/AAAAAAAAAAI/AAAAAAAALjI/RXPwcr9hGaE/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "They have a small selection of beers on tap, the service at the bar is a bit slow but friendly! If you come during happy hour you will catch the live music and great deals too! Also on the first Thursday of the month they have a Ducati bike night! Highly recommend that day and around 6 pm!", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (131, "Kunal Uppal", 5, "https://lh4.googleusercontent.com/-03Afs0lzds4/AAAAAAAAAAI/AAAAAAAAADI/MUEGngHO_Cw/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "What a wonderful place for a brunch. Right by the water or inside, as u may please. Cocktails on point and breakfast menu to kill for. Brilliant place for nice relaxed sunny winter mornings...", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (131, "Cyrus Nazarian", 5, "https://lh3.googleusercontent.com/-XS-XMVowwgQ/AAAAAAAAAAI/AAAAAAAAOus/Ik_ykQIHSEc/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "An old San Francisco staple that never disappoints. The food is delicious, the bar tenders are very friendly and knowledgeable, the view is nothing to scoff at, and the art on the walls is entertaining. Word of advice: be kind to staff and patrons, don't be a jackass, and have a good time. Pretty sure the kitchen staff are wizards, because the food was freaking tasty!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (132, "Richard Redding", 3, "https://lh5.googleusercontent.com/-JlZIkyNGr_g/AAAAAAAAAAI/AAAAAAAAANo/sWeQdEjzDTc/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Good food but expensive. A full breakfast with OJ and coffee costs $20. I was a tourist needing breakfast in downtown San Francisco, and there seemed to be very few choices within walking distance of Union Square. I tried Honey Honey and ordered the 2+2+2 platter -- 2 eggs, 2 pancakes, 2 slices of bacon. The orange juice ($5 extra) was fresh-squeezed on the spot, and was delicious, but it was served at room temperature, not chilled. I was satisfied with the breakfast, very tasty, but wanted to give a note of caution about the prices.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (132, "Tim Saunders", 4, "https://lh6.googleusercontent.com/-z0xgdoHMrG8/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjU85vsagGZcA7bBzjf10JIKNpIG1Q/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "There was a huge queue to get in, then another queue to order. Overall it must have taken 45 minutes to order. I am not sure if this is because it was a holiday, or if it is like this every weekend. There is a good selection of vegetarian options, although too much avocado for my liking. Still it wasn't hard to find something to my liking. When the food arrived it was tasty, although a bit on the cold side. I might have gone for some dessert, but that would mean queuing up again. You have to know what you will want when you first order.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (132, "Kyle Maxwell", 4, "https://lh3.googleusercontent.com/-fIr9ItMFnNE/AAAAAAAAAAI/AAAAAAAAE6E/MYa0GFXFx5M/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Big menu, and pretty affordable. Order at the counter and they will serve you your food. I didn’t get an option for a side with my sandwich. I got a California sandwich and an almond cookie for around $12. Decent service and decent atmosphere. If I lived closer it would be a place to go when I couldn’t decide.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (132, "Monica Carlton", 5, "https://lh5.googleusercontent.com/-k5vVvXWfC1g/AAAAAAAAAAI/AAAAAAAAAG8/33O5xxoJsD4/s128-c0x00000000-cc-rp-mo/photo.jpg", "Had the honey honey cake with some coffee which was very yummy but make sure you like really sweet food. Both the sweet and savory crepes are great. The service is quick and the servers and bussers are nice and efficient and helped us get a table once we were at the counter ordering.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (132, "Joel Beachum", 5, "https://lh6.googleusercontent.com/-JrTf8n60GgE/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVcLb9PN-ZcHvBhkFbW8gTK3miGHw/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "This place is so good and they have a nice selection for breakfast. Lots of choices, but you can definitely find something good. Eggs are stove of the best. Crepes are excellent. Staff is very helpful and very cool looking atmosphere.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (133, "Kevin Jaskela", 5, "https://lh6.googleusercontent.com/-lUvwpMs7LIk/AAAAAAAAAAI/AAAAAAAAHUE/_fd_YUaEtBo/s128-c0x00000000-cc-rp-mo/photo.jpg", "Hands down a favorite in SF. Nestled in the heart of Jackson Square's design district, this chic restaurant begs for your time. The service is always impeccable and the food - out of this world. Every time, I want to try each item on the menu, but never have enough time, nor the appetite to handle such a daunting task. The ambiance is beautiful at any time of the day with seating options to suit anyone's fancy. I'd like to meet ONE person that doesn't like Italian food and show them Cotogna. Go watch the chefs in action through the window from outside and they'll have to ask you to leave since you'll be fogging up the glass with your tongue hanging out and breath on the window. Trust me - you're jaw will drop with every bite. I say, let it hang!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (133, "Serena Qiu", 4, "https://lh5.googleusercontent.com/-Nux80MjB2l8/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXu4qUGzjHMqwUmYomYtudcYGHaJA/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "My husband and I had the ravioli with shrimp and their famous pork chop dish. The pork chop on the menu was actually pork belly. A bit fattening. We had to cut out quite a few of the fatty bits. But the taste is really good. The ravioli on the other hand is absolutely amazing. The shrimp sauce is rich but not overly done. The service is okay. Overall, it is a bit pricey but certainly a great restaurant.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (133, "Michelle Moon", 5, "https://lh3.googleusercontent.com/-4MWcEoAcmS0/AAAAAAAAAAI/AAAAAAAASkc/Wc-8opV6xXE/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "From ambience and decor to the perfectly executed pastas and pizzas, to the detailed attentive service... you can't go wrong. Lunches are just as crowded as dinners so make sure to make a rez in advance.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (133, "Inna Baglayeva", 5, "https://lh3.googleusercontent.com/-UDCnBgtHGGY/AAAAAAAAAAI/AAAAAAAAIPw/9ouZaBoPcAA/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "The best dinner I ate in a long time. Everything was perfect. Try will mushroom pizza you will not regret it.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (133, "Harith Albadri", 1, "https://lh5.googleusercontent.com/-5Ykug1fNuZE/AAAAAAAAAAI/AAAAAAAAADc/HtadRnTpGG8/s128-c0x00000000-cc-rp-mo/photo.jpg", "Unfortunately, none of the staff can make seating a smooth experience. After asking for a seat, we were offered the bar or chef’s counter(great! No problem for a party of 2 :) ) and told to wait 20 min, we were told we’d get a phone call, after an hour of wandering North Beach, no call.. so we walk back, and arrive to to a rude staff, some non-sense about a table being cleared and then obviously we were just walked to barren bar. Bottom line, businesses that lie when they fall short and insult you in front of other customers to save face don’t deserve the investor money that makes their comical existence possible.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (134, "Kelsey Sones", 1, "https://lh3.googleusercontent.com/-G5ngUyvBvQM/AAAAAAAAAAI/AAAAAAAAAAw/UoXrIbfWETQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "Had a really bad experience overall. Seating was really fast, and it’s a very nice inside which I was grateful for but our waitress....... She talked ONCE the whole time we were here. She asked for our order and my husband gave his order and she walked away and I had to wave at her 3 times so I could order my food.. I ordered some ranch and she spilled it all over the table and walked off. She didn’t ask if we wanted refills, if the food was okay, if we needed anything. Just set our food down and walked away. Didn’t see her again at our table until she brought the check, and she still didn’t speak. I wanted my first time here in SF at the Hard Rock Cafe to be a nice experience but she ruined it for my husband and I. We will not be coming back in the future.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (134, "Scott Moore", 4, "https://lh3.googleusercontent.com/-_NSOvPKMdp0/AAAAAAAAAAI/AAAAAAAAX2c/LfBVJf-_xW4/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "I got to say I was surprised. Reviews were not very good for this place. However they cooked my burger properly. The bartenders worked together and never let our drinks go dry. We forgot out receipt for the limited edition glass and the bartender went right over and to the cashier and took care of us. The only reason I took a star off was the bathroom was a little dirty and smelly.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (134, "Cris G", 1, "https://lh4.googleusercontent.com/-YjRn2CfOJKI/AAAAAAAAAAI/AAAAAAAAAFE/Ynmv1PDpHqM/s128-c0x00000000-cc-rp-mo/photo.jpg", "Came to eat here because a family member recommend it. Overall, horrible service! I waited a total of 1hr & 40mins. The waiters ignored us for 30mins before coming to help us. I asked for some sauce with my order and The waiter that was helping us didn't bring it. I reminded him and he never brought it. I had to ask another waiter and he also couldn't do the simple job. The food was good don't get me wrong but they took too long. I suggest that if you are willing to wait as long as I did then go for it... if not just go somewhere else. Very disappointed of this place. I give it 0 stars not even one.... had to add the star on this because if I don't it won't post. ", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (134, "Charlie Kopicz", 4, "https://lh5.googleusercontent.com/-C-MCh3Is-Vw/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjV2nHlxvApHvzIFt-PuBnHz1x9_xQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "The food was fantastic. Our waitress was very concerned to get us outstanding service. Anyone in this area should stop in for good food.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (134, "Sam Mitchell", 1, "https://lh6.googleusercontent.com/-3Futdlb1vdY/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVgNEdy6VVPXHcfZBLUIK_53JNqpA/s128-c0x00000000-cc-rp-mo/photo.jpg", "Just had dinner with my fiance and 2 friends on our first night in SF. Overall, the food was the usual for a Hard Rock Cafe anywhere. The service on the other hand was down right disappointing, especially after an outstanding experience at the New York cafe just last week. Drinks didn't get refilled at all during the meal, nor did we get any water for the table. Staff did not take away our plates after we had finished, we sat and continued to chat for close to an hour with dirty plates and empty drinks still sitting there. Nobody seemed to care. Very disappointed and won't be back.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (135, "Raquel B", 4, "https://lh6.googleusercontent.com/-K7tb75ZQx_Y/AAAAAAAAAAI/AAAAAAAAHD4/h7iWowyLOg0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "This is an old school concept with servers wearing suits and you're tucked away from other diners in your own big leather booth. Hearty meal with nice pastas, meats (veal is my favorite), salad, and soup options. Decent wine and cocktail menu. Front room is always fun to drop in for a drink and people watch.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (135, "Steve Heard", 5, "https://lh5.googleusercontent.com/-hO48I4rHFK0/AAAAAAAAAAI/AAAAAAAAAMI/M5uuQwBoX20/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "One of my favorite spots in San Francisco. Never had a bad meal or bad service. They are consistently good. Several different dining rooms, including a counter, hidden in the back. My favorite place to hang out is in the spacious bar, if I can get a seat by the fireplace. Great, cozy, spot for people watching, having a drink, and the full menu is served as well. Reasonable prices, good food, friendly servers.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (135, "Dina Mercer", 2, "https://lh5.googleusercontent.com/-BVU6DsN0GIY/AAAAAAAAAAI/AAAAAAAAAA0/ljc9qNB2tPI/s128-c0x00000000-cc-rp-mo/photo.jpg", "The Caesar salad was good. Calamari were good. Hamburger was ok, and Joe’s Special, their signature dish was terrible! The spinach was bitter, there was hardly any egg and it tasted burnt. I’ve made Joe’s Special at home so much better. Not planning on ever returning.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (135, "David J Bohnet", 5, "https://lh3.googleusercontent.com/--1dAcE24aNE/AAAAAAAAAAI/AAAAAAAAArI/FxB8OOjPaNc/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Love this spot! Great crab Benedict seriously! Original Irish coffee and a great bar! History and local feel mixed with great food and service! Bravo!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (135, "Matt Traywick", 5, "https://lh5.googleusercontent.com/-45EwtbHPmXc/AAAAAAAAAAI/AAAAAAAA6Ww/CHDSgNJrDvU/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Yes. They serve Italian food. But I go there for one of the best burgers and fries in town. LOUD...in a good way. And remember when restaurants had real PROFESSIONAL waiters? Well, they work here now.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (136, "Joe Sassaman", 3, "https://lh5.googleusercontent.com/-YO94zE0Kj7w/AAAAAAAAAAI/AAAAAAAAGSQ/Pu-wvsYQ7AU/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Not horrible but definitely average. Could have / should have been better. There are so many great restaurants in SF that this place to to step up their game. Not coming back", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (136, "Gustavo Bermeo", 5, "https://lh4.googleusercontent.com/-lx6hG8JIyHU/AAAAAAAAAAI/AAAAAAAAhOQ/AKxHd6DmRAc/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Great atmosphere, nice craft drink selection and the bartenders are very knowledgeable. Small food menu but it's fantastic. A must stop when in SF.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (136, "Rig Daddy", 5, "https://lh5.googleusercontent.com/-VxxaYH73mB8/AAAAAAAAAAI/AAAAAAAAODs/0j6w4wKdMds/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "We love this place. Really great food. Nice atmosphere. Quiet! Service that is quick but not intrusive. My friends like the drink menu and wine list.", "4 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (136, "Matthew Moore", 4, "https://lh6.googleusercontent.com/-z3gy5lPN_nI/AAAAAAAAAAI/AAAAAAAAEgE/2pqiB9qvOEU/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "The service was great the food was great. I had the burger and it was cooked to perfection. The only issue was the lighting, it was so dim that we had to use the light on my phone to read the small type", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (136, "Andrew Wilson", 4, "https://lh3.googleusercontent.com/-FCR0clK3C5U/AAAAAAAAAAI/AAAAAAAAGXE/zDD1pHICjWA/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "A reliable restaurant in the SOMA District. Food is very well prepared and the ingredients are always farm fresh.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (137, "Alyscia Richards", 5, "https://lh6.googleusercontent.com/-Xb7ewzaNxKw/AAAAAAAAAAI/AAAAAAAAABo/Gs4PrHYJy78/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great beer, cocktails and pub fare in SF. Fun for the whole family as they also have a great kids menu that my daughter adores and they always gives her paper and crayons to color with. Fantastic service and gorgeous space. Additionally a great place to catch games without the screens interfering with a friendly, homey atmosphere.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (137, "Aleksandr T", 3, "https://lh4.googleusercontent.com/-jaa2sx3dXns/AAAAAAAAAAI/AAAAAAAAAEw/2EO8ncyL_90/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Very touristy Irish bar. The food quality is ok, nothing impressive. They have a great location near Union square, this is why it's popular with tourists. Definitely more of a pricey option for dining. Their Guinness is of decent quality. Staff was ok, nothing exceptional and not horrible either. Better options nearby but this place is quite large and can seat larger groups if that is what you need.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (137, "Eldar Islamovic", 5, "https://lh3.googleusercontent.com/-EgpKdXYTqaw/AAAAAAAAAAI/AAAAAAAAAKs/9Ay1WKfu-ms/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Great beer selection, delicious food and amazing service. Friendly, prompt servers. Restrooms are clean. Really cool interior of an old building. We had a pulled pork sandwich with fries and a fried chicken sandwich with carrot-ginger soup. Both were fantastic! 9/10", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (137, "Maria Gary", 5, "https://lh4.googleusercontent.com/-XGD5aBi2ysc/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjW-lTtiGOAQzOrv70ghO-k50pkm8g/s128-c0x00000000-cc-rp-mo/photo.jpg", "Got in here right as they were supposed to close kitchen. Service superb. Sat us, got drinks, took food order and our perfectly cooked filet Mignon were delivered within twelve minutes. Great place.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (137, "Alec Boliver", 5, "https://lh4.googleusercontent.com/-muqAwYCZGxA/AAAAAAAAAAI/AAAAAAAAAVE/ufHTupIs1GU/s128-c0x00000000-cc-rp-mo/photo.jpg", "This bar is great! They had excellent music and a great atmosphere. The bartender was friendly and helpful even on a busy Saturday night", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (138, "Joseph Ingoglia", 5, "https://lh4.googleusercontent.com/-xeGOZs8PqY0/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVCFPeFIdh92iKHduI3-f6VButm9A/s128-c0x00000000-cc-rp-mo/photo.jpg", "I just cannot say enough of this place. I must have recommended this restaurant to at least over 50 people. The food is good as any high quality expensive restaurant in San Francisco. But it is so much more affordable. When I am visiting San Francisco this is my go-to place if I want to have something great to eat. This is not a five star restaurant it's a 10 Star restaurant in my opinion. The servers are very nice. And the decor and Ambience is super. Do not miss going to this restaurant if you are in San Francisco. Truthfully Joe", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (138, "Billy Light", 3, "https://lh4.googleusercontent.com/-5Mmyh3Epp_8/AAAAAAAAAAI/AAAAAAAAABU/WB1bYuFXgDY/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "The food is pretty dang good. I like their cause and it seems that they’re seeing results from it. These guys are obviously not seasoned waiters as you do need a level of patience With them. You’ll tend to wait a while on simple things like your drinks getting refilled or the timing of your food coming out. They’re always nice and courteous. I’ve enjoyed my meals from here", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (138, "Greg Smith", 5, "https://lh3.googleusercontent.com/-GNRp2e4QhXA/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXKAPJ34zNBEEc-dg7bdqLshnPImw/s128-c0x00000000-cc-rp-mo/photo.jpg", "This is the best kept non-secret in SF. Everything about this place is fantastic! The setting, right on the Embarcadero; the restaurant itself is very classy while allowing casual attire; the food is expertly prepared, tasty and at a price that makes you think you’re looking at a menu from the 70’s! And last, but definitely not least is the staff...professional, courteous and very service oriented. The only way you could go wrong with this place is to NOT go there. Breakfast , lunch or dinner you’ll be glad you tried it.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (138, "Claire Lochner", 4, "https://lh4.googleusercontent.com/-__-2tSBx6kg/AAAAAAAAAAI/AAAAAAAAOLs/fSQb1fRacm0/s128-c0x00000000-cc-rp-mo/photo.jpg", "Good quiet atmosphere on a rainy Thursday night. Their hot fudge is delicious and the leg of lamb falls off the bone. :) Their deep fried mashed potatoes and marinara were also really good. Very well priced apps and desserts won't break the bank.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (138, "Beverly Boone", 4, "https://lh5.googleusercontent.com/-fF0yelr0KpI/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVZS29sWiXW2T36875Up7qws77Cmg/s128-c0x00000000-cc-rp-mo/photo.jpg", "Always great. Love this place. Reasonably priced with great ambiance. Recommend it highly.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (139, "Sofia Sodhi", 5, "https://lh3.googleusercontent.com/-hIpc83mQoEc/AAAAAAAAAAI/AAAAAAAAADk/fK4cAzzT7eY/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Hell yeah! This place is great! They serve great food, like burgers and sushi. They even have delicious desserts such as ice cream sandwiches with cookies made in-house. Please go here. I beg you. If you're on the fence about this place, give it a try. If you don't like it, you need to see a doctor who checks taste buds.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (139, "Vipin Kumar", 5, "https://lh4.googleusercontent.com/-YYLwnQJU4Fw/AAAAAAAAAAI/AAAAAAAAAAA/MY073tOMdb0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "If your'e going in a larger group then you have a better chance of trying out all the burgers. However, if you're going in a small group of two or three people, I suggest to try a slider. A normal sized burger is 6 ounces and costs about $12/$13 while sliders are 3 pieces of 2 ounces each and cost the same $12/$13 but the advantage is you can try more burgers in one go. While there can be one slider, the others can order one burger each. For me and my friend, we did a slider, a normal burger and truffle fries. Must haves - Umami burger and Truffle fries. Happy hour made the truffle fries possible for just $3. Glutton heaven.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (139, "Johnny Ma", 5, "https://lh4.googleusercontent.com/-khpNVG-fEWA/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUfJZYVom-8G0nY5PLUH_bSwEUrnA/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Incredibly delicious burgers. This is one of the few places that serves the impossible meat, if no one told me it wasn't meat I would never know the difference, the only way to really tell is to have another burger side by side for comparison. The truffle burger is to die for, if you are a truffle lover, this is burger alone is worth the visit. The place is a bit pricey, I would go back only when im really craving that truffle burger or when I want to take my vegetarian friends to a burger joint.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (139, "Henry Mckenzie", 4, "https://lh6.googleusercontent.com/-sEEnxYuq9Gc/AAAAAAAAAAI/AAAAAAAAAAo/O52OR7Eo7JU/s128-c0x00000000-cc-rp-mo/photo.jpg", "Burger is solid, bacon is thick and ample but rarely crispy enough to my taste. It's worth trying! Friendly and quick service.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (139, "Verna Bennett", 4, "https://lh5.googleusercontent.com/-BhKuyhyzPpc/AAAAAAAAAAI/AAAAAAAAAAo/drCtHH9NoNQ/s128-c0x00000000-cc-rp-mo/photo.jpg", "Outstanding! BRAVO!!! Perfect - FRESH Much positive has been reported and it's all true. I have never had a burger this good.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (140, "Melissa Smith", 5, "https://lh3.googleusercontent.com/-uCxTsJ-mt50/AAAAAAAAAAI/AAAAAAABNh0/5V-qniWLNBE/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "This place always has fun themed events (mac & cheese day, tater tot day, etc) featuring local food trucks and vendors; always a delicious time when you stop in! I usually skip the bottomless beer since there isn’t quite enough going on to spend a great amount of time, but to each their own!", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (140, "Sarah Chandler", 5, "https://lh5.googleusercontent.com/-KGGQ6raJaXw/AAAAAAAAAAI/AAAAAAAAN4A/zOO3CJwiZi4/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "My favorite truck is the empanadas truck and it is here often! There is also a nice covered eating area that's warm. We love coming here when the weather is nice. There are restrooms in a temporary building, they are clean-ish, definitely serviceable. All in all it's a nice food truck park with lots of options and absolutely delightful on a sunny day.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (140, "stephanie stott", 4, "https://lh4.googleusercontent.com/-FCIOkqAlhJY/AAAAAAAAAAI/AAAAAAAAUdY/5HvpGsUcmQs/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Overall I love the variety here and the events they host. Location-wise it can be a little bit rough to find parking (especially if you aren't comfortable parallel parking). I usually come here when it's packed. However, I went last weekend during the day when it was super empty and pigeons had their run of the place -- hopping on tables, chairs, anywhere there could have been scraps. It made me a bit uncomfortable knowing a table I chose to eat at could have recently had been walked all over by birds. I'm unsure how often the tables and chairs are disinfected at Soma StrEat Food Park but something to be aware of.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (140, "Samantha Mallory", 5, "https://lh3.googleusercontent.com/-fu5NVOi-8bE/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUH4oNGtSziGn7nafEjIYsIDCqXmg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Loved this place! Super cute and wide food variety. Absolutely a must stop. Will be back here.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (140, "James Cachelin", 4, "https://lh4.googleusercontent.com/-YtV1WjmkBxk/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjX_7snODLPk1VzAtnPtwmx3-zSvBA/s128-c0x00000000-cc-rp-mo/photo.jpg", "This place is super cool, great food and awesome atmosphere. The value isn't the best overall, but that changes truck to truck. Great for a quick bite in San Fran.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (141, "Daniel Gefen", 4, "https://lh4.googleusercontent.com/-2Ef1iXBn8f4/AAAAAAAAAAI/AAAAAAABMKw/pwrwR5ncN5A/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Place is cool. Nice selection of pastries and deserts, although there are small flies constantly flying near the pastries. I think the place should do something about it. I ordered the breakfast bowl and it was really good. Staff could be a bit more friendly.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (141, "yuchiao wang", 5, "https://lh5.googleusercontent.com/-5wFlzRXv1BE/AAAAAAAAAAI/AAAAAAAAAzY/Ibn7kb6lKqk/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Amazing place. ‘Rebel within’ is awesome! Great atmosphere, very good service!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (141, "Kevin V", 5, "https://lh3.googleusercontent.com/-pocTDv4Vk2I/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXdC8jyEuuvfWa-W_Ie9jRVMT3t0Q/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "I had their banana bread during their half-off happy hour (4-5pm for breakfast items). It was moist, flavorful, but not too sweet. The delicate combination of flavors were a wonderfully executed.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (141, "Диана Капран", 5, "https://lh4.googleusercontent.com/-RuV3KgxEm4U/AAAAAAAAAAI/AAAAAAAAABM/B-ulfZ0q3PY/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Creamy latte with excellent espresso and milk. A lot of fresh pastries and some interesting desserts. Fast service!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (141, "J Wu", 5, "https://lh6.googleusercontent.com/-RfR4J7Yt_EI/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXRABFQ3eJ9aguHa2HreXug4H6MMg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Unique selection of pastries and desserts. They also serve some savory items, and of course coffee, tea and other hipster fare. I prefer these pastries to Tartine down the street, for their interesting flavor pairings and smaller queue/line.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (142, "Karthik Subramaniam", 5, "https://lh3.googleusercontent.com/-dURrsHFf_o4/AAAAAAAAAAI/AAAAAAAAeOg/0tG5Sy-VEzU/s128-c0x00000000-cc-rp-mo-ba6/photo.jpg", "Food: wide range of dishes to choose from, delicious food.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (142, "melinda moy-elliott", 4, "https://lh5.googleusercontent.com/-QZHVtqDi3go/AAAAAAAAAAI/AAAAAAAAAtI/_-IwjEHkHpg/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "A place that can definitely get crowded during the lunch rush. Try to make reservations if you can. Good spot for an intimate or group lunch or dinner. Service is quick and friendly and food comes out quickly.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (142, "helen pinto", 5, "https://lh4.googleusercontent.com/-eMwsun_TOBY/AAAAAAAAAAI/AAAAAAAAOmU/Ztzz5EKAB_0/s128-c0x00000000-cc-rp-mo/photo.jpg", "Love it! Food was fantastic, authentic and beautiful. We came in 20 mins prior to closing but they were very sweet and told us we'd just have to order fast...no problem for us. I cannot wait to come back and I can't wait to recommend to others.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (142, "Christina Lee", 5, "https://lh4.googleusercontent.com/-jyRmrMWX6sQ/AAAAAAAAAAI/AAAAAAAAjtA/p-0wN9xa2C4/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Service was really slow, but as someone who doesn't usually have anywhere to be, it didn't bother me. The food was excellent if you like things that are spicy. I had the spicy eggplant and really enjoyed it. The mango shaved ice was also great.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (142, "Angelie Khu", 5, "https://lh6.googleusercontent.com/-1OCnSo1wnZ0/AAAAAAAAAAI/AAAAAAAAAB4/xiJGehLqxwE/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Beautiful decor! The food was amazing too. Must try are the garlic fried chicken wings, tom yum soup and the durian shaved ice for dessert... so good!", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (143, "Patsy Gonzales", 4, "https://lh4.googleusercontent.com/-NSTuLimILzA/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjUHRXvu1J6JZ6CtAxJGPnvq7sh1Yg/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Very friendly service they always have what you want. The food is great !", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (143, "Lee Rosen", 3, "https://lh3.googleusercontent.com/-Q7S1HWF57MI/AAAAAAAAAAI/AAAAAAAABaE/nnSZbHKY7Yc/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "Salads, soups and sandwiches that are consistent - nothing super special, but many healthy choices that are tasty. Not as fond of their bagles - too airy, not the right chew.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (143, "H Williams", 1, "https://lh6.googleusercontent.com/-W7uZCqv6qi0/AAAAAAAAAAI/AAAAAAAABKE/ui1fAFn_J04/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "It's what it is. A sad but needed utility, pre crafted bland food there when you need it.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (143, "Oscar Hernandez", 5, "https://lh4.googleusercontent.com/-LC86b2F0IMk/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjULoYpxhbl_QkokvS8AFgo-U2C3vw/s128-c0x00000000-cc-rp-mo/photo.jpg", "Couldn’t get better service then at panera bread  . Got a free pastry just for signing up and got my order ready in less then 5 mins", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (143, "Allison Vennerberg", 4, "https://lh5.googleusercontent.com/-IGWlq3gFhyg/AAAAAAAAAAI/AAAAAAAAHUU/oomDjnY_Otc/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Friendly staff, good bagels. Ample amount of seating. Clean bathrooms.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (144, "Ryan Blodgett", 5, "https://lh5.googleusercontent.com/-vAnK1McdYdA/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXsoNUIJzaxweU9CFAboPtupMQv5g/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Excellent quality seafood, which is expected when that's all a restaurant offers. We received personable service from bartender Emily despite being busy, with knowledgeable wine pairing recommendations. We were able to snag the last two bar stools before the Friday dinner rush (we're sad we just missed their happy hour); if you prefer a table reservations are recommended.", "a week ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (144, "Emily Crow", 5, "https://lh4.googleusercontent.com/-eI0phBF6ntc/AAAAAAAAAAI/AAAAAAAAALs/5U1VrQMstR8/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Wow, really fantastic. The crudo sampler and chowder are exquisite. So nicely done. Fun place and super friendly people both at and behind the bar. Recommended by a friend, really glad I went.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (144, "Yara", 5, "https://lh3.googleusercontent.com/-4d7HuG1kVGw/AAAAAAAAAAI/AAAAAAAAASM/pnFjuoqxtnM/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "If you like raw fish, you will not be disappointed. The food is delicious and creative. The portions are quite small but so yummy. Great wine as well. Lovely staff and nice decor.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (144, "the donger the donger", 5, "https://lh6.googleusercontent.com/-EsPWAEWKNH4/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjU0ETp4FVXgFnxnp4jMR_jCHzbbqg/s128-c0x00000000-cc-rp-mo/photo.jpg", "The good: Excellent happy hour, delicious food, friendly and patient staff, nice location, comfortable restaurant. The bad: Difficult to find close parking....", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (144, "Stephan Rabimov", 5, "https://lh4.googleusercontent.com/-VxqieAW4crM/AAAAAAAAAAI/AAAAAAAAWqc/9yMexfUzFWM/s128-c0x00000000-cc-rp-mo/photo.jpg", "Head here for happy hour to experience SF’s best seafood - from oysters to clams to chowder. #approved PS: Slayer was the best server ever, polite, prompt, and full of smile.", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (145, "Michael Piccirilli", 5, "https://lh4.googleusercontent.com/-guTHzu4GkZ8/AAAAAAAAAAI/AAAAAAAAAYc/j-_N5yY-VYs/s128-c0x00000000-cc-rp-mo/photo.jpg", "Great pizza. Get the deep dish. Good for dining in or take out. It can get busy so if you plan on eating there be prepared to wait for a table.", "2 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (145, "Nicola Sicchieri", 5, "https://lh6.googleusercontent.com/-wOI_dc6e4uc/AAAAAAAAAAI/AAAAAAAAWUg/dcTmc833w1Q/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Love the deep dish with cornmeal crust! It’s not that heavy as the traditional flour dough and it has a very characteristic taste. They are quite fast which is rare for deep dish pizza place.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (145, "Catherine Marylander", 5, "https://lh4.googleusercontent.com/-dP2mDJwOvNs/AAAAAAAAAAI/AAAAAAAAFYI/JQoHIPgrMNA/s128-c0x00000000-cc-rp-mo/photo.jpg", "THIS IS MY FAVORITE DEEP DISH OF ALL TIME! And definitely my favorite pizza in San Francisco. Great vibes - perfect for a casual date night with a bottle of wine or a night out with a few friends. The Classic Deep Dish is AMAZING.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (145, "Michelle Mellish", 5, "https://lh5.googleusercontent.com/-cIRIFjicL74/AAAAAAAAAAI/AAAAAAAATAo/aZXyZEUY0js/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "30 minutes wait for deep dish is well worth it! Not stodgy or greasy, just really great pizza. Good beer and cocktails with friendly service. New favourite pizza in SF", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (145, "Karina Sangster", 5, "https://lh3.googleusercontent.com/-OBITG15GdPQ/AAAAAAAAAAI/AAAAAAAAtFY/Oa1ZxblUZKQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "This was an AMAZING PLACE.
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (146, "John Minervini", 5, "https://lh6.googleusercontent.com/-XmFw_mNf12E/AAAAAAAAAAI/AAAAAAAANBI/0MnIRhkwOQY/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Oh my God. The chorizo/fried egg pizza is my jam. Tasty cocktails. Great for lunch on a weekday, when you won't have to wait (long). Wait staff is chill/friendly/very professional. In many ways this place embodies San Francisco for me. Trendy crowd.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (146, "Jimmy Chan", 4, "https://lh4.googleusercontent.com/-3OTdvt_U3no/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXfappvSO-w_Rt8otkV5XX07SSnLA/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Came here for restaurant week so will have to return for their regular menu which I've heard good things about. The cauliflower soup and chicken liver pate were my favorites. The fried chicken po boy was just alright. The brioche bun was dry, the chicken didn't have much flavor and the Cole slaw tasted bitter to me. Friendly service and cute decor they have nice heaters which made the patio area tolerable even on a chilly day.", "4 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (146, "frances chau", 5, "https://lh6.googleusercontent.com/-UZAtE1bumCU/AAAAAAAAAAI/AAAAAAAAAWE/uRVVkGji0M4/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "I love the decor and atmosphere of this place! They have an adorable outdoor sitting with heaters in the back of the place as well as in front. The food was pretty good, a little on the expensive side, but really tasty stuff that I can justify paying a little more for.", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (146, "John James", 5, "https://lh6.googleusercontent.com/-TyALt8h7yJo/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXE1iwI9NLWmITYf8KJfAu-V7WCfg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "An awesome place for taking a vegetarian friend. Have no fear, there's still some meat dishes on the menu but its healthy, fresh, quick and surprisingly reasonable when it comes to the final bill. Great spot in the Castro.", "3 weeks ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (146, "Anna Nickelson", 5, "https://lh4.googleusercontent.com/-o0z1FvpygtE/AAAAAAAAAAI/AAAAAAAAAE0/bDZIlpjs2zg/s128-c0x00000000-cc-rp-mo/photo.jpg", "I took a large group (40) to Starbelly for a corporate dinner - we rented out the back patio. The group had a wonderful time and I got nothing but compliments on the restaurant choice! The managers and staff made the whole process so easy - they were incredibly accommodating when our group size changed, were very helpful with dietary restrictions, and overall wonderful. The wait staff was professional, quick, easy to work with, and very flexible. The food is amazing! Definitely try the pasta with jalapenos and bacon - we asked for the bacon on the side to make it vegetarian-friendly. We ate up every bite of what we were served - there was more than enough food, but it was so tasty we couldn't stop! Thank you to Starbelly & staff for an awesome night! EDIT: I have since been back on my own dime for dinner and drinks. They have delicious cocktails for only $9 (which is a steal in SF!). The Starbelly pizza is definitely my favorite, along with the burratta toast.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (147, "nick austin", 5, "https://lh6.googleusercontent.com/-XnHiZ1ur5TQ/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXg928CyvMuLgk08cMjF0f5tJs61Q/s128-c0x00000000-cc-rp-mo/photo.jpg", "For some reason i would never order a steak here, and i would only come for dinner. However i have had the rack of lamb, porkchop, duck breast, and veal chop: all excellent. The creme brulee was big and thick, yet lacking flavor (but still good). The carrot cake was great though. The nutty irishman was a good cap off. Good service, as you would expect from a place like this. Overall a 5-star experience: I recommend!", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (147, "Daniel R", 5, "https://lh3.googleusercontent.com/-GPsx_ir5ff8/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVmk2IOyHyILZ2A6u35WgWUN7n3Tg/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Amazing service with great food. Wonderful experience.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (147, "Worker Kim", 5, "https://lh4.googleusercontent.com/-7qNBPGDTvF8/AAAAAAAAAAI/AAAAAAAACDs/sVRI6FRNZtE/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Amazing atmosphere and an unforgettable dining experience. I will definitely go back when i visit San Francisco. ", "6 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (147, "Adam Matthews", 5, "https://lh5.googleusercontent.com/-0mueWnr57yY/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjW9Xsvt_GqCsKYHw7_VnTTO1pi4MA/s128-c0x00000000-cc-rp-mo/photo.jpg", "Awesome food (filet was amazing), pricey but huge portions (baked potato was a total mutant from a giant potato planet), solid wine list. If you're a carnivore, worth a visit. ", "7 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (147, "Chris Alex", 3, "https://lh6.googleusercontent.com/-MhTzPPHvdTI/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjW9cZge9wE1xBkOjccmWRC5qo74lw/s128-c0x00000000-cc-rp-mo-ba5/photo.jpg", "Very little flavor to anything here. Service is slow but I think it might be the kitchen rather than service staff. For the prices and quality of food I'd recommend going elsewhere. ", "8 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (148, "April Kee", 3, "https://lh6.googleusercontent.com/-xEHqudkCGzo/AAAAAAAAAAI/AAAAAAAABG8/nuGuqSgqQcM/s128-c0x00000000-cc-rp-mo/photo.jpg", "Embarcadero location: I wish the employees would eat somewhere else. Not to sound mean but it is annoying. They are loud and don’t really care about the customers. Have some decorum. Food is delicious otherwise and I would have given it more stars if not for the said experience. Thanks.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (148, "Tasneem Nomanbhai", 4, "https://lh3.googleusercontent.com/-WJNl0B-_A-s/AAAAAAAAAAI/AAAAAAAAD5Y/RBbPc3T9vfg/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "I'm not a tourist, but I've been here a few times. There are 3 locations right by the water. Their food is good, albeit overpriced. I think soup bowl is a waste because there is very little soup and lots of bread that people end up discarding. Such a waste. However, the bread is delicious. I liked their veggie sandwich the most - so much so the second time around I had them add some turkey to the veggie sandwich and it was perfect. Get a side cup of tomato soup instead - you'll get more for your money.", "3 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (148, "william crouch", 5, "https://lh6.googleusercontent.com/-B7fMgarcv_k/AAAAAAAAAAI/AAAAAAAADqc/u7_9n0c4cd0/s128-c0x00000000-cc-rp-mo-ba4/photo.jpg", "Good food and good service. Crowded at peak times with little inside seating space. The Snickerdoodle cookies are the bomb.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (148, "James Smith", 2, "https://lh6.googleusercontent.com/-gU7nWOnD8qM/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjVPhRLlZwW7wY_JpnV_TMW6kuHLGQ/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Every clam chowder I've ever had is better than this. Grill cheese was charred. Rude red headed guy at the front can be profitably replaced with an automated kiosk.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (148, "Dhananjay Mani", 1, "https://lh6.googleusercontent.com/-fptCsisqfjQ/AAAAAAAAAAI/AAAAAAAAAAA/ACSILjXiBWKWgE4oDyK6cgrchPjpBqvTzw/s128-c0x00000000-cc-rp-mo/photo.jpg", "We went to this place for lunch and we order both The purist avocado and The pesto avocado toast. I also ordered Toasted California club and California Veggie Sandwich. I don't have any complaint about food except my order was sitting on the counter for 15 minutes. I was standing there and the guy who was serving orders had no idea my orders are sitting there. He was confused between different orders however I observed they are using order management system. I don't know why he was so confused. After waiting and looking at my order sitting on the counter for 15 minutes, he called my number and gave me additional food item that was not ordered by me. I told him this is not order and he got offended. I am giving one star because of poor service. Other than that food was aright.", "5 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (149, "Mary C", 3, "https://lh4.googleusercontent.com/-IcWtJqYAYe8/AAAAAAAAAAI/AAAAAAAAABQ/UScB7xyz2GU/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "We were an early reservation so the place wasn't full. We were seated on the top level of the restaurant and seated towards the very back of the room with the most lighting, we should've got better seating, especially with a friend who is SF food critic at the table. It wasn't the most elegant part of the restaurant. It took at least 30 minutes to be served a glass of water-basically the ordered drinks came first. The group I came with were sitting there waiting too long for a glass of water. Always offer water before you can get to the real drink list. Sitting there with nothing is never a way to start a supposed fine dining experience. For the most part the dining experience was okay. I ordered their ribeye steak medium rare, I've ordered it this way in many restaurants and have only had one other experience where I could say the temperature of my steak wasn't done right. I was served a very rare steak, however they were very accommodating in taking it back and cooking it a little bit more. Which I never like to do because I never want to offend the chef nor have any chef do anything to my meal. When it came back from the kitchen it was pretty delightful, a little bit more cooked than I would have liked-but very good- compliments of the chef!! Probably the worst experience was going to the bathroom. For what I would call a pretty nice place-I was a little bit thrown off by the bathroom-they could do better, and keep it much cleaner. There were mistakes like my dessert coming with raspberry sauce instead of Carmel, but hey no one is perfect. Roy's is nice, but I'd only go back if someone really wanted to go.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (149, "Detach Brands (@drinkdetach)", 5, "https://lh4.googleusercontent.com/-Ml4qe51as4Y/AAAAAAAAAAI/AAAAAAAAAAc/IRsNsPAOu2w/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "I have been here many times for bothboersonal and business dinners. I have mostly had both stellar experience with the food and the service. On occasion there may be some slower service when very busy but still acceptable. The tomahawk we had was phenomenal, a bit large on portion size but easily shared among a few. Other dishes including sides were equally as good as were the desserts.", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (149, "Mindy Greenberg", 2, "https://lh4.googleusercontent.com/-8bHEKDyfmAI/AAAAAAAAAAI/AAAAAAAARqI/inMzpaqamvA/s128-c0x00000000-cc-rp-mo/photo.jpg", "Was shocked at how terrible the food was... I went to the one in LA years ago and it was awesome. Bf and I were both super disappointed.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (149, "Keven Adams", 4, "https://lh3.googleusercontent.com/-32kg1d2SbBk/AAAAAAAAAAI/AAAAAAAABsw/HQCHV_yaG6w/s128-c0x00000000-cc-rp-mo/photo.jpg", "The atmosphere was great , the food was great. The service was outstanding. I had the fish and I don't think I've had fish better ever", "2 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (149, "Vanissa Kendrick", 5, "https://lh5.googleusercontent.com/-rbGCm1dKg-A/AAAAAAAAAAI/AAAAAAABHNc/nH4wM7V_Jr0/s128-c0x00000000-cc-rp-mo/photo.jpg", "Will was an amazing server. Food was great!!!", "in the last week");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (150, "Chris Onesto", 4, "https://lh5.googleusercontent.com/-lS0CGv5vLCg/AAAAAAAAAAI/AAAAAAAAA4w/wcwTdm8R-pY/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "It's chipotle. So ya. The really nice thing is that they're really fast here. Also they're nice. The restaurant itself is nicely laid out and clean and up to date. Pro tip. Order 2 chicken tacos and it costs under $7.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (150, "Joe G.", 5, "https://lh3.googleusercontent.com/-dQIPLfuYkWw/AAAAAAAAAAI/AAAAAAAAACw/P42cOkTaauM/s128-c0x00000000-cc-rp-mo/photo.jpg", "Don’t go to Kearny st. Location. This one on market is far better.", "a month ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (150, "Sword of Damocles", 5, "https://lh6.googleusercontent.com/-YnCIbXD_HZI/AAAAAAAAAAI/AAAAAAAAABY/dx2PVVpkjIo/s128-c0x00000000-cc-rp-mo-ba3/photo.jpg", "Great food experience! Clean and high quality products that will take your taste buds to heaven. Forget the michelin star places, this is better.", "6 months ago");
INSERT INTO reviews (restaurant_id, author_name, rating, profile_photo_url, review_text, relative_time_description) VALUES (150, "Rocky Able", 3, "https://lh4.googleusercontent.com/-8desXusdNZo/AAAAAAAAAAI/AAAAAAAAACE/F9iMmnFrRJc/s128-c0x00000000-cc-rp-mo-ba2/photo.jpg", "To find nice place to eat in San Francisco is quite hard but Chipotle is always the solution. Their menu isn't huge but everyone can find something to eat , they even have two choices for vegan/vegetarian which I was very surprised. Food is tasted and reasonable for the amount. Just one bad side of this fast food is that one employee wasn't patient with us. It was our first time to try and we don't know how it worked. We understand they need to work hard but please be more patient with people and try to explain to them the concept! By doing this I am sure you will earn more customers who will come back to this store, right?", "10 months ago");
